// Initialize flow context
let flowContext = flow.get('flowContext') || {};

//------------------------------------------------------------------------------------------
// +++ L1.0.1 - UTILITY FUNCTIONS -------------------------------------------------------------

// \\\ FUNCTION: log --------------------------\\\
function log(functionName, ...messages) {
    const timestamp = new Date().toISOString();
    console.log(`[${timestamp}] [${functionName}]`, ...messages);
}

// \\\ FUNCTION: parseDynamicVariables --------------------------\\\
function parseDynamicVariables(text, flowContext) {
    log('parseDynamicVariables', 'Starting function with text:', text);

    // Check if text is a string, convert if possible
    if (typeof text !== 'string') {
        log('parseDynamicVariables', 'Warning: text is not a string. Type:', typeof text);
        if (text === null || text === undefined) {
            log('parseDynamicVariables', 'Text is null or undefined. Returning empty string.');
            return '';
        }
        try {
            text = String(text);
            log('parseDynamicVariables', 'Converted text to string:', text);
        } catch (error) {
            log('parseDynamicVariables', 'Error converting text to string:', error);
            return '[Error: Unable to process non-string input]';
        }
    }

    if (!flowContext || typeof flowContext !== 'object') {
        log('parseDynamicVariables', 'Warning: flowContext is not a valid object');
        return text;
    }

    try {
        // Handle previousResponse placeholder
        if (text.includes('{previousResponse}')) {
            log('parseDynamicVariables', 'Found {previousResponse} placeholder in text');

            if (msg.promptSectionContext && typeof msg.promptSectionContext.currentStep === 'number') {
                const currentStep = msg.promptSectionContext.currentStep;
                const previousStep = currentStep - 1;
                if (previousStep >= 0 && msg.promptSectionContext.responseArray[previousStep]) {
                    const previousResponse = msg.promptSectionContext.responseArray[previousStep];
                    log('parseDynamicVariables', `Found previousResponse for step ${previousStep}:`, previousResponse);
                    // Ensure responseText is a string
                    let stringResponseText = String(previousResponse);
                    text = text.replace(/{previousResponse}/g, stringResponseText);
                } else {
                    log('parseDynamicVariables', `No previousResponse found for step ${previousStep}`);
                    text = text.replace(/{previousResponse}/g, '[Previous response not available]');
                }
            }
            log('parseDynamicVariables', 'After replacing previousResponse:', text);
        }

        // Handle $<<variable>>$ placeholders
        flowContext = flow.get("flowContext");
        text = text.replace(/\$<<(\w+)(\d+)Result>>\$/g, (match, cmdType, cmdNumber) => {
            console.log(`[${new Date().toISOString()}] [parseDynamicVariables] Processing match: ${match}`);
            console.log(`[${new Date().toISOString()}] [parseDynamicVariables] Command Type: ${cmdType}, Command Number: ${cmdNumber}`);

            console.log(`[${new Date().toISOString()}] [parseDynamicVariables] Full dynamicCmdContext:`, JSON.stringify(flowContext.dynamicCmdContext, null, 2));

            if (flowContext.dynamicCmdContext) {
                let cmd;

                // Search for the command in the commandsArray or single command
                if (Array.isArray(flowContext.dynamicCmdContext.commandsArray)) {
                    console.log(`[${new Date().toISOString()}] [parseDynamicVariables] Searching in commandsArray`);
                    cmd = flowContext.dynamicCmdContext.commandsArray.find(c => 
                        c.type === cmdType && c.number.toString() === cmdNumber.toString()
                    );
                } else if (flowContext.dynamicCmdContext.command) {
                    console.log(`[${new Date().toISOString()}] [parseDynamicVariables] Checking single command`);
                    const singleCmd = flowContext.dynamicCmdContext.command;
                    if (singleCmd.type === cmdType && singleCmd.number.toString() === cmdNumber.toString()) {
                        cmd = singleCmd;
                    }
                }

                // If command found, return its result
                if (cmd) {
                    console.log(`[${new Date().toISOString()}] [parseDynamicVariables] Matching command found:`, JSON.stringify(cmd, null, 2));

                    if (cmd.result !== undefined && cmd.result !== null) {
                        console.log(`[${new Date().toISOString()}] [parseDynamicVariables] Replaced ${match} with:`, cmd.result);
                        return String(cmd.result);
                    } else {
                        console.log(`[${new Date().toISOString()}] [parseDynamicVariables] Command found but result is undefined or null`);
                    }
                } else {
                    console.log(`[${new Date().toISOString()}] [parseDynamicVariables] No matching command found for type: ${cmdType} and number: ${cmdNumber}`);
                }
            } else {
                console.log(`[${new Date().toISOString()}] [parseDynamicVariables] dynamicCmdContext is not defined`);
            }

            console.log(`[${new Date().toISOString()}] [parseDynamicVariables] Warning: ${match} not available in flowContext`);
            return `[${cmdType}${cmdNumber}Result not available]`;
        });

        // Replace $<<execute(...)>>$ with executed code result
        text = text.replace(/\$<<execute\((.*?)\)>>\$/g, (match, code) => {
            log('parseDynamicVariables', 'Executing code:', code);
            try {
                code = code.replace(/;$/, '');
                const safeContext = createSafeContext(flowContext);
                const result = new Function('flowContext', `with(flowContext) { return (${code}); }`)(safeContext);
                log('parseDynamicVariables', 'Execution result:', result);
                return result !== undefined ? result : '[undefined]';
            } catch (error) {
                log('parseDynamicVariables', 'Error executing:', error.message);
                return `[Error executing: ${error.message}]`;
            }
        });

        // Handle regular flowContext property access
        text = text.replace(/\$<<([\w.]+)>>\$/g, (match, path) => {
            log('parseDynamicVariables', 'Accessing flowContext property:', path);
            const parts = path.split('.');
            let value = flowContext;
            for (const part of parts) {
                if (value && typeof value === 'object' && part in value) {
                    value = value[part];
                } else {
                    log('parseDynamicVariables', `Warning: ${path} not available in flowContext`);
                    return `[${path} not available]`;
                }
            }
            const result = typeof value === 'object' ? JSON.stringify(value) : String(value);
            log('parseDynamicVariables', 'Context property value:', result);
            return result;
        });

        log('parseDynamicVariables', 'Final result:', text);
        return text;
    } catch (error) {
        log('parseDynamicVariables', 'Unexpected error:', error);
        return `[Error: ${error.message}]`;
    }
}

// \\\ FUNCTION: createSafeContext --------------------------\\\
function createSafeContext(flowContext) {
    return {
        flow: {
            get: (key) => {
                const value = flow.get(key);
                log('createSafeContext', `flow.get('${key}'):`, value);
                return value;
            },
            set: (key, value) => {
                log('createSafeContext', `flow.set('${key}'):`, value);
                flow.set(key, value);
            }
        },
        global: {
            get: (key) => {
                const value = global.get(key);
                log('createSafeContext', `global.get('${key}'):`, value);
                return value;
            },
            set: (key, value) => {
                log('createSafeContext', `global.set('${key}'):`, value);
                global.set(key, value);
            }
        },
        flowContext: flowContext
    };
}

//------------------------------------------------------------------------------------------
// +++ L1.0.2 - COMMAND EXECUTION FUNCTIONS -------------------------------------------------------------

// \\\ FUNCTION: executeLocalCmd --------------------------\\\
function executeLocalCmd(command, flowContext) {
    log('executeLocalCmd', 'Executing local command:', command);
    let result = '';
    try {
        const commandString = command.commands.join('\n');
        log('executeLocalCmd', 'Executing:', commandString);
        const safeContext = createSafeContext(flowContext);
        const wrappedCommand = `with(this) { ${commandString} }`;
        const commandResult = new Function(wrappedCommand).call(safeContext);
        result = commandResult !== undefined ? commandResult : '';
    } catch (error) {
        log('executeLocalCmd', 'Error:', error.message);
        result = `[Error: ${error.message}]`;
    }
    log('executeLocalCmd', 'Result:', result);
    return result;
}

// \\\ FUNCTION: executeCompileOrPackCmd --------------------------\\\
function executeCompileOrPackCmd(command, flowContext) {
    log('executeCompileOrPackCmd', 'Executing compile/pack command:', command);
    let result = '';

    if (command && Array.isArray(command.commands)) {
        result = command.commands.map(cmd => {
            return parseDynamicVariables(cmd, flowContext);
        }).join('\n');
    } else {
        log('executeCompileOrPackCmd', 'Invalid command structure:', command);
        result = '[Error: Invalid command structure]';
    }

    log('executeCompileOrPackCmd', 'Result:', result);
    return result.trim();
}

// \\\ FUNCTION: executeMainPromptCmd --------------------------\\\
function executeMainPromptCmd(command, flowContext) {
    log('executeMainPromptCmd', 'Executing main prompt command:', command);
    const result = command.commands.map(cmd => parseDynamicVariables(cmd, flowContext)).join('\n');
    log('executeMainPromptCmd', 'Result:', result);
    return result;
}

// \\\ FUNCTION: waitForSidePromptResult --------------------------\\\
function waitForSidePromptResult(timeout = 300000) {
    log('waitForSidePromptResult', 'Starting to wait for side prompt result');

    return new Promise((resolve, reject) => {
        const startTime = Date.now();
        const checkSidePromptResult = () => {
            if (flow.get('waitingForSidePrompt') == false) {
                log('waitForSidePromptResult', 'Side prompt result is ready');
                let sidePromptResult = flow.get('sidePromptResult') || {};
                resolve(sidePromptResult);
            } else if (Date.now() - startTime > timeout) {
                log('waitForSidePromptResult', 'Timeout reached while waiting for side prompt result');
                reject(new Error('Timeout waiting for side prompt result'));
            } else {
                log('waitForSidePromptResult', 'Side prompt result not ready, checking again in 1000ms');
                setTimeout(checkSidePromptResult, 1300);
            }
        };
        checkSidePromptResult();
    });
}

// \\\ FUNCTION: executeSidePromptCmd --------------------------\\\
async function executeSidePromptCmd(command, flowContext) {
    log('executeSidePromptCmd', 'Executing side prompt command:', command);
    const sidePromptCommands = command.commands;

    // Set flow level variable indicating side prompt processing
    flow.set('waitingForSidePrompt', true);

    // Execute side prompt commands
    const result = sidePromptCommands.map(cmd => parseDynamicVariables(cmd, flowContext));

    log('executeSidePromptCmd', 'result' , result);

    // Prepare and send the side prompt message
    let sidePromptMsg = {
        sidePromptContext: {
            promptSectionsArray: result,
            status: "start"
        }
    };

    node.send([null, sidePromptMsg])

    log('executeSidePromptCmd', 'Sent side prompt, waiting for result');

    try {
        // Wait for the side prompt result
        const sidePromptResult = await waitForSidePromptResult();

        // Update flow flowContext with side prompt result
        flowContext.processedResult = sidePromptResult;
        flowContext.processedResultReady = true;
        flow.set('flowContext', flowContext);

        log('executeSidePromptCmd', 'Received side prompt result:', sidePromptResult);
        return sidePromptResult;
    } catch (error) {
        log('executeSidePromptCmd', 'Error waiting for side prompt result:', error);
        return `[Error: ${error.message}]`;
    } finally {
        // Reset the processing flag
        flow.set('waitingForSidePrompt', false);
    }
}

// \\\ FUNCTION: executeCommand --------------------------\\\
async function executeCommand(command, flowContext) {
    log('executeCommand', 'Executing command:', command);
    let result = '';

    try {
        switch (command.type) {
            case 'localCmd':
                result = await executeLocalCmd(command, flowContext);
                break;
            case 'compileCmd':
                result = await executeCompileOrPackCmd(command, flowContext);
                break;
            case 'packCmd':
                result = await executeCompileOrPackCmd(command, flowContext);
                break;
            case 'sidePromptCmd':
                result = await executeSidePromptCmd(command, flowContext);
                break;
            case 'mainPromptCmd':
                result = await executeMainPromptCmd(command, flowContext);
                break;
            default:
                log('executeCommand', 'Unknown command type:', command.type);
                result = `[Unknown command type: ${command.type}]`;
        }
    } catch (error) {
        log('executeCommand', 'Error executing command:', error);
        result = `[Error: ${error.message}]`;
    }

    log('executeCommand', 'Result:', result);
    return result;
}

//------------------------------------------------------------------------------------------
// +++ L1.0.3 - FLOW CONTEXT AND OUTPUT HANDLING -------------------------------------------------------------

// \\\ FUNCTION: determineOutputType --------------------------\\\
function determineOutputType(commandType) {
    switch (commandType) {
        case 'sidePromptCmd': return 'sidePrompt';
        case 'mainPromptCmd': return 'mainPrompt';
        default: return 'dynamicCmd';
    }
}

// \\\ FUNCTION: updateFlowContext --------------------------\\\
function updateFlowContext(flowContext, command, result) {
    flowContext.processedResult = result;
    flowContext.processedResultReady = true;

    // Update the result in the commandsArray
    if (Array.isArray(flowContext.dynamicCmdContext.commandsArray)) {
        const commandIndex = flowContext.dynamicCmdContext.commandsArray.findIndex(
            cmd => cmd.type === command.type && cmd.number === command.number
        );
        if (commandIndex !== -1) {
            flowContext.dynamicCmdContext.commandsArray[commandIndex].result = result;
        }
    }

    // Update the responseArray in sidePromptContext
    if (flowContext.sidePromptContext && Array.isArray(flowContext.sidePromptContext.responseArray)) {
        const currentStep = flowContext.sidePromptContext.currentStep;
        if (currentStep >= 0 && currentStep < flowContext.sidePromptContext.responseArray.length) {
            flowContext.sidePromptContext.responseArray[currentStep] = {
                ...flowContext.sidePromptContext.responseArray[currentStep],
                response1: result
            };
        }
    }

    flow.set('flowContext', flowContext);

    log('updateFlowContext', `Updated flowContext with result for ${command.type}${command.number}`);
    log('updateFlowContext', `Updated flowContext: ${flowContext}`);
}

