// \\\ FUNCTION: main --------------------------\\\
async function main() {
    console.log('starting Main');
    log('commandExecutor main', 'Starting main function');

    // Log the relevant parts of the msg object
    log('commandExecutor main', 'Received Msg:', JSON.stringify(msg, null, 2));

    const { dynamicCmd, command, triggerCommandExecution } = msg.dynamicCmdContext || {};

    // Log individual properties
    log('commandExecutor main', 'dynamicCmd:', dynamicCmd);
    log('commandExecutor main', 'command:', JSON.stringify(command, null, 2));
    log('commandExecutor main', 'triggerCommandExecution:', triggerCommandExecution);

    // Handle different message types
    if (!dynamicCmd && !msg.sidePromptContext.status) {
        log('commandExecutor main', 'Passing through non-dynamic command');
        return { ...msg, dynamicCmdContext: { ...msg.dynamicCmdContext, currentOutputType: 'passThrough' } };
    }
    else if (msg.sidePromptContext.status === "Sequence Complete") {
        // Reset the processing flag & pass results
        log('commandExecutor main', 'Reset Flag');
        flow.set('waitingForSidePrompt', false);
        flow.set('sidePromptResult', msg.payload);
        return null;
    }
    else if ((!command || !triggerCommandExecution) && !msg.sidePromptContext.status) {
        log('commandExecutor main', 'Invalid dynamic command, passing through');
        return msg;
    }

    // Process valid dynamic command
    log('commandExecutor main', 'Processing dynamic command');
    let finalResult, currentOutputType = 'dynamicCmd';

    try {
        // Execute the command and determine output type
        finalResult = await executeCommand(command, msg);
        currentOutputType = determineOutputType(command.type);

        // Update the result in the command
        command.result = finalResult;
        msg.dynamicCmdContext.currentOutputType = currentOutputType;
    } catch (error) {
        log('commandExecutor main', 'Error executing command', error);
        finalResult = `[Error: ${error.message}]`;
        currentOutputType = 'error';
    }

    log('commandExecutor main', 'Final result', finalResult);
    log('commandExecutor main', 'Current output type', currentOutputType);

    // Update flowContext and return processed result
    updateFlowContext(flowContext, command, finalResult);
    return {
        ...msg,
        dynamicCmdContext: {
            ...msg.dynamicCmdContext,
            command: { ...command, result: finalResult },
            processedResult: finalResult,
            currentOutputType,
        }
    };
}

