async function runPromptSection() {
  console.log('[Run Prompt Side] Entering runPromptSection');
  try {
    // Reload page if necessary and set up for new chat
    await reloadPageIfNecessary(0);

    const page = await getCurrentPage();
    const newChatButton = await page.$('nav > a.flex.py-3.px-3.items-center.gap-3.rounded-md:has-text("New chat")');
    if (newChatButton) {
        await newChatButton.click();
        await page.waitForLoadState('networkidle');
    }
    console.log('[Run Prompt Side] Got current page in runPromptSection');

    console.log('[Run Prompt Side] Full Msg:', msg);
    await clickButtonIfVisible('button:has-text("Continue generating")', true);
    const promptText = msg.sidePromptContext.promptConfig.body || 'Sidefiring';
    console.log('[Run Prompt Side] Entering prompt:', promptText);
    await page.waitForSelector('#prompt-textarea', { state: 'attached', timeout: 30000 });
    await page.fill('#prompt-textarea', promptText);

    // Handle side prompt if present
    if (msg.sidePromptContext && msg.sidePromptContext.sidePromptCmdDone) {
      console.log('[Run Prompt Side] Handling sidePrompt');
      const sidePromptText = msg.sidePromptContext.sidePromptCmdDone;
      await page.evaluate((text) => {
        const textarea = document.querySelector('#prompt-textarea');
        textarea.value += '\n\n' + text;
        textarea.dispatchEvent(new Event('input', { bubbles: true }));
      }, sidePromptText);
    }

    // Send prompt and wait for response
    await page.click('button[data-testid="send-button"]');
    console.log('[Run Prompt Side] Sent prompt');

    await waitForResponseCompletion();

    // Extract and process response
    const responseText = await extractResponseText(page);
    await clickButtonIfVisible('button:has-text("I prefer this response")');
    await page.waitForSelector('button[data-testid="stop-button"]', { state: 'hidden', timeout: 120000 });

    nodeContext.sidePromptContext.responseArray = nodeContext.sidePromptContext.responseArray || [];

    // Ensure responseText is a string
    let stringResponseText = String(responseText);

    // Add the new response to the array
    nodeContext.sidePromptContext.responseArray.push(stringResponseText);
    context.set('nodeContext', nodeContext);
    console.log('nodeContext.sidePromptContext.responseArray :', nodeContext.sidePromptContext.responseArray);

    console.log('[Run Prompt Side] Exiting runPromptSection');
    return responseText;
  } catch (error) {
    console.log(`[Run Prompt Side] Error in runPromptSection: ${error.message}`);
    console.log('[Run Prompt Side] Error stack:', error.stack);
    throw error;
  }
}

// \\\ FUNCTION: runPromptSectionWithRetries --------------------------\\\
async function runPromptSectionWithRetries(msg, maxRetries) {
    for (let retryCount = 0; retryCount < maxRetries; retryCount++) {
        if (isStopRequested()) {
            return handleStopRequest();
        }

        try {
            const payload = await runPromptSection();
            return {
                payload,
                status: { fill: "green", shape: "dot", text: "done" }
            };
        } catch (error) {
            console.error(`[Run Prompt Main] Attempt ${retryCount + 1} failed:`, error);
            await reloadPageIfNecessary(retryCount + 1);

            if (retryCount === maxRetries - 1) {
                throw error;
            }
        }
    }
    throw new Error('Max retries reached');
}

