function calculateArbitrage(oddsOutcome1, oddsOutcome2, globalStake) {
    let line1Value = americanToDecimal(oddsOutcome1);
    let line2Value = americanToDecimal(oddsOutcome2);


    // Convert string to float for safety
    oddsOutcome1 = parseFloat(line1Value);
    oddsOutcome2 = parseFloat(line2Value);

    // Determine the lowest odds and calculate stakes
    let riskForLowestOdds = globalStake; // The stake for the outcome with the lowest odds is set to $100
    let lowestOdds = Math.min(oddsOutcome1, oddsOutcome2);
    let highestOdds = Math.max(oddsOutcome1, oddsOutcome2);


    // Calculate Arbitrage Percentage
    let arbPercentageTotal = (1 / oddsOutcome1 + 1 / oddsOutcome2) * 100;

    let result = {}; // Initialize the result object

    // Determine If There Is an Arbitrage Opportunity
    if (arbPercentageTotal < 100) {
        // Adjust the other stake proportionally to maintain arbitrage
        let riskOnHighestOdds = (lowestOdds / highestOdds) * riskForLowestOdds;

        // Round stakes to the nearest dollar
        let roundedRiskOnLowestOdds = Math.round(riskForLowestOdds);
        let roundedRiskOnHighestOdds = Math.round(riskOnHighestOdds);

        // Calculate total required stake to maintain arbitrage proportions
        let totalRoundedStake = roundedRiskOnLowestOdds + roundedRiskOnHighestOdds;

        // Calculate total required stake to maintain arbitrage proportions
        let ArbTotalStake = roundedRiskOnLowestOdds + roundedRiskOnHighestOdds;

        // Calculate potential returns for each bet
        let returnOnLowestOdds = roundedRiskOnLowestOdds * lowestOdds;
        let returnOnHighestOdds = roundedRiskOnHighestOdds * highestOdds;

        // Calculate profits for each outcome by subtracting the total stake from the returns of each outcome
        let ArbProfit1 = returnOnLowestOdds - ArbTotalStake;
        let ArbProfit2 = returnOnHighestOdds - ArbTotalStake;


        // Populate the result object with values
        result.ArbOpportunity = true;
        result.ArbPercentage = 100 - arbPercentageTotal.toFixed(2);
        result.ArbBetOnOutcome1 = roundedRiskOnLowestOdds.toFixed(2);
        result.ArbBetOnOutcome2 = roundedRiskOnHighestOdds.toFixed(2);
        result.ArbTotalStake = totalRoundedStake.toFixed(2);
        result.ArbProfit1 = ArbProfit1.toFixed(2);
        result.ArbProfit2 = ArbProfit2.toFixed(2);

    } else {
        // Indicate no arbitrage opportunity in the result object
        result.ArbOpportunity = false;
        result.message = "No Arbitrage Opportunity.";
    }

    return result;
}

