// General function to extract and sort messages by a specified property path
function extractAndSortByProperty(messages, propertyPath) {
    // Split the property path into parts for nested properties
    const pathParts = propertyPath.split('.');

    // Map and extract the specified property value, handling nested properties
    const extractedValues = messages.map((message, index) => {
        let value = message.transform2Message;
        for (const part of pathParts) {
            value = value ? value[part] : undefined;
        }
        return { messageIndex: index, value };
    });

    // Filter out any undefined values that may not exist for the specified property
    const filteredValues = extractedValues.filter(item => item.value !== undefined);

    // Sort the filtered array by the specified property in descending order
    filteredValues.sort((a, b) => b.value - a.value);

    return filteredValues;
}


