// Helper function to get the function name from a node
function getFunctionName(node) {
    return node.id ? node.id.name : (node.key ? node.name : null);
}

// Extract all top-level async functions
const topLevelAsyncFunctions = [];
ast.body.forEach(node => {
    if (node.type === 'FunctionDeclaration' && node.async) {
        topLevelAsyncFunctions.push(node);
    } else if (node.type === 'VariableDeclaration') {
        node.declarations.forEach(declaration => {
            if (declaration.init && declaration.init.type === 'FunctionExpression' && declaration.init.async) {
                topLevelAsyncFunctions.push(declaration.init);
            }
        });
    }
});

if (topLevelAsyncFunctions.length === 0) {
    node.error('No top-level async functions found.');
    return null;
}

let newFileContent = fileContent;
let changesMade = false;

// First, gather the inner functions for each top-level async function
const functionData = topLevelAsyncFunctions.map(funcNode => {
    const functionName = getFunctionName(funcNode);
    if (!functionName) return null;

    node.warn('Function found: ' + functionName);

    // Extract top-level inner function names
    const innerFunctionNames = [];
    const innerFunctionRegex = /async function (\w+)\s*\(.*?\)\s*{/g;
    let functionBody = fileContent.slice(funcNode.body.start, funcNode.body.end);
    let innerFunctionMatch;
    while ((innerFunctionMatch = innerFunctionRegex.exec(functionBody)) !== null) {
        // Ensure that the function is directly within the top-level function and not named "execute" or "default"
        const functionStartIndex = innerFunctionMatch.index;
        const precedingText = functionBody.slice(0, functionStartIndex);
        const openBraces = (precedingText.match(/{/g) || []).length;
        const closeBraces = (precedingText.match(/}/g) || []).length;
        const innerFunctionName = innerFunctionMatch[1];
        if (openBraces === closeBraces + 1 && innerFunctionName !== "execute" && innerFunctionName !== "default") {
            innerFunctionNames.push(innerFunctionName);
        }
    }
    node.warn('Top-level inner function names: ' + innerFunctionNames.join(', '));

    return { funcNode, innerFunctionNames };
}).filter(Boolean);

// Helper function to insert the innerFunctions object
function insertInnerFunctions(funcNode, innerFunctionNames, offset) {
    if (innerFunctionNames.length === 0) {
        return 0; // No inner functions to insert
    }

    // Check if 'innerFunctions' is already declared within the function body
    const functionBody = fileContent.slice(funcNode.body.start + offset, funcNode.body.end + offset);
    const innerFunctionsDeclared = /const innerFunctions = {([^}]*)}/.exec(functionBody);

    if (innerFunctionsDeclared) {
        const existingInnerFunctions = innerFunctionsDeclared[1];
        const existingFunctions = existingInnerFunctions.split(',').map(fn => fn.trim()).filter(fn => fn.length > 0);
        const missingFunctions = innerFunctionNames.filter(fn => !existingFunctions.includes(fn));
        if (missingFunctions.length === 0) {
            node.warn(`All inner functions already declared in ${getFunctionName(funcNode)}, skipping insertion.`);
            return 0; // No adjustment needed
        } else {
            node.warn(`Updating inner functions in ${getFunctionName(funcNode)}.`);
            const updatedInnerFunctionsArray = `const innerFunctions = {\n    ${[...new Set([...existingFunctions, ...missingFunctions])].join(',\n    ')}\n  };`;

            newFileContent = newFileContent.replace(innerFunctionsDeclared[0], updatedInnerFunctionsArray);
            changesMade = true;
            return updatedInnerFunctionsArray.length;
        }
    }

    // Prepare the innerFunctions object to be inserted
    const innerFunctionsArray = `
  // #region : 0. Inner Functions
  const innerFunctions = {
    ${innerFunctionNames.join(',\n    ')}
  };
  // #endregion`;

    // Insert innerFunctions at the beginning of the main function body
    const functionBodyStartIndex = funcNode.body.start + offset + 1; // +1 to skip the opening '{'
    newFileContent = newFileContent.slice(0, functionBodyStartIndex) + innerFunctionsArray + newFileContent.slice(functionBodyStartIndex);
    changesMade = true;
    return innerFunctionsArray.length;
}

// Insert the innerFunctions object for each top-level async function sequentially
let adjustmentOffset = 0;
functionData.forEach(({ funcNode, innerFunctionNames }) => {
    adjustmentOffset += insertInnerFunctions(funcNode, innerFunctionNames, adjustmentOffset);

    // Add a line in each inner function to add itself to innerFunctions (only for 2nd layer or deeper)
    const innerFunctionRegex = /async function (\w+)\s*\(.*?\)\s*{/g;
    let functionBody = newFileContent.slice(funcNode.body.start + adjustmentOffset, funcNode.body.end + adjustmentOffset);
    let innerFunctionMatch;
    while ((innerFunctionMatch = innerFunctionRegex.exec(functionBody)) !== null) {
        const innerFunctionName = innerFunctionMatch[1];
        const innerFunctionBodyStartIndex = innerFunctionMatch.index + innerFunctionMatch[0].length + funcNode.body.start + adjustmentOffset;

        // Determine the indentation of the inner function
        const lineStartIndex = newFileContent.lastIndexOf('\n', innerFunctionMatch.index + funcNode.body.start + adjustmentOffset) + 1;
        const line = newFileContent.slice(lineStartIndex, innerFunctionMatch.index + funcNode.body.start + adjustmentOffset);
        const indentation = line.match(/^\s*/)[0];

        // Check if this inner function is on the first layer or named "execute" or "default"
        const precedingText = functionBody.slice(0, innerFunctionMatch.index);
        const openBraces = (precedingText.match(/{/g) || []).length;
        const closeBraces = (precedingText.match(/}/g) || []).length;
        const isFirstLayer = openBraces === closeBraces + 1;
        const isExcludedFunction = innerFunctionName === "execute" || innerFunctionName === "default_exec";

        // Add the line with the determined indentation (only if not on the first layer and not an excluded function)
        if (!isFirstLayer && !isExcludedFunction) {
            const addToInnerFunctionsLine = `\n${indentation}  innerFunctions.${innerFunctionName} = ${innerFunctionName};\n`;
            const updatedInnerFunctionBody = newFileContent.slice(0, innerFunctionBodyStartIndex) + addToInnerFunctionsLine + newFileContent.slice(innerFunctionBodyStartIndex);

            // Only add the line if it doesn't already exist
            if (!newFileContent.includes(`innerFunctions.${innerFunctionName} = ${innerFunctionName}`)) {
                newFileContent = updatedInnerFunctionBody;
                changesMade = true;
                adjustmentOffset += addToInnerFunctionsLine.length;
            }
        }
    }

    // Update the AST to rescan the adjusted content again
    try {
        ast = acorn.parse(newFileContent, { ecmaVersion: 2020, sourceType: "module" });
    } catch (e) {
        node.error('Error parsing the updated file content: ' + e.message);
        return null;
    }
});

// Log new file content length for debugging
node.warn('New file content length: ' + newFileContent.length);

// Only write back to the file if changes were made
if (changesMade) {
    msg.payload = newFileContent;
    msg.filename = msg.filename; // Use the original file path
    node.warn('Writing changes to the file.');
    return [msg, null];
} else {
