    node.log('User root paths: ' + JSON.stringify(userPaths));

    // Store the user paths in the global context
    global.set('userRootPaths', userPaths);

    return userPaths;
}

function indexTargetFolders(rootPaths, targetFolders) {
    const indexedPaths = {};

    for (const [rootKey, rootPath] of Object.entries(rootPaths)) {
        node.log(`\nIndexing root path: ${rootKey} - ${rootPath}`);
        indexedPaths[rootKey] = {};

        for (const folder of targetFolders) {
            const fullPath = path.join(rootPath, folder);
            node.log(`Checking target folder: ${fullPath}`);

            if (fs.existsSync(fullPath)) {
                node.log(`Indexing contents of: ${fullPath}`);
                indexedPaths[rootKey][folder] = indexDirectory(fullPath);
            } else {
                node.log(`Target folder not found: ${fullPath}`);
            }
        }
    }

    // Store the indexed paths in the global context
    global.set('indexedPaths', indexedPaths);

    return indexedPaths;
}

function indexDirectory(dirPath, baseObj = {}) {
    const items = fs.readdirSync(dirPath, { withFileTypes: true });

    items.forEach(item => {
        if (item.isDirectory()) {
            node.log(`Found directory: ${path.join(dirPath, item.name)}`);
            baseObj[item.name] = { type: 'directory', path: path.join(dirPath, item.name), contents: {} };
            indexDirectory(path.join(dirPath, item.name), baseObj[item.name].contents);
        } else {
            node.log(`Found file: ${path.join(dirPath, item.name)}`);
            baseObj[item.name] = { type: 'file', path: path.join(dirPath, item.name) };
        }
    });

    return baseObj;
}

function countItems(obj) {
    const counts = { file: 0, directory: 0 };
    function count(obj) {
        for (const item of Object.values(obj)) {
            if (item.type === 'file') {
                counts.file++;
            } else if (item.type === 'directory') {
                counts.directory++;
                count(item.contents);
            } else if (typeof item === 'object') {
                count(item);
            }
        }
    }
    count(obj);
    return counts;
}

// Main function to be executed when the node receives a message
function main() {
    node.log('Starting indexing process...');
    const userRootPaths = getUserRootPaths();
    const indexedPaths = indexTargetFolders(userRootPaths, TARGET_FOLDERS);

    node.log('\nIndexed structure:');
    node.log(JSON.stringify(indexedPaths, null, 2));

    const counts = countItems(indexedPaths);

    node.log('\nTotal counts:');
    node.log(`Files: ${counts.file}`);
    node.log(`Directories: ${counts.directory}`);

    // Prepare the output message
    outputMsg.payload = {
        indexedPaths: indexedPaths,
        counts: counts
    };

    return outputMsg;
}

// This is the entry point for the Node-RED function node
return main();

