// The initialParser Layer

function parseInputText(inputText) {
    console.log(`[parseInputText] Starting to parse input text`);
    console.log(`[parseInputText] Input text length: ${inputText.length} characters`);

    const lines = inputText.split('
');
    console.log(`[parseInputText] Number of lines: ${lines.length}`);

    let dynamicCmd = { sections: [] };
    let currentCommand = null;
    let isCapturingNestedCommand = false;
    let nestedCommandBuffer = '';

    console.log(`[parseInputText] Initialized parsing variables`);

    const processLine = (line, index) => {
        console.log(`[parseInputText] Processing line ${index + 1}: '${line}'`);

        const originalLine = line;
        line = line.replace(/\\/\\/.*$/, '').trim(); // Remove comments

        if (line !== originalLine) {
            console.log(`[parseInputText] Removed comment. New line: '${line}'`);
        }

        if (line === 'dynamicCmd') {
            dynamicCmd.sections.push({ type: 'commandSet', commands: [] });
            console.log(`[parseInputText] Detected 'dynamicCmd' section. Created new command set. Total sections: ${dynamicCmd.sections.length}`);
            return;
        }

        if (line === '- commandSet') {
            console.log(`[parseInputText] Detected commandSet line, ignoring.`);
            return;
        }

        const mainCommandMatch = line.match(/^-\\s*(\\w+)(\\d+)/);
        if (mainCommandMatch) {
            const [, cmdType, cmdNumber] = mainCommandMatch;
            currentCommand = { type: cmdType, number: cmdNumber, commands: [] };
            dynamicCmd.sections[dynamicCmd.sections.length - 1].commands.push(currentCommand);
            console.log(`[parseInputText] Started new main command: ${cmdType}${cmdNumber}. Total commands in current section: ${dynamicCmd.sections[dynamicCmd.sections.length - 1].commands.length}`);
        } else if (line.startsWith('- `')) {
            isCapturingNestedCommand = true;
            nestedCommandBuffer = line.substring(3);
            console.log(`[parseInputText] Started capturing nested command. Initial buffer: '${nestedCommandBuffer}'`);
        } else if (isCapturingNestedCommand) {
            if (line.endsWith('`')) {
                nestedCommandBuffer += line.substring(0, line.length - 1);
                currentCommand.commands.push(nestedCommandBuffer);
                isCapturingNestedCommand = false;
                console.log(`[parseInputText] Finished capturing nested command: '${nestedCommandBuffer}'`);
                console.log(`[parseInputText] Total commands for ${currentCommand.type}${currentCommand.number}: ${currentCommand.commands.length}`);
            } else {
                nestedCommandBuffer += line + '
';
                console.log(`[parseInputText] Capturing nested command. Current buffer length: ${nestedCommandBuffer.length} characters`);
            }
        } else if (line.startsWith('-') && currentCommand) {
            const subCommand = line.substring(1).trim();
            currentCommand.commands.push(subCommand);
            console.log(`[parseInputText] Added sub-command to ${currentCommand.type}${currentCommand.number}: '${subCommand}'`);
            console.log(`[parseInputText] Total commands for ${currentCommand.type}${currentCommand.number}: ${currentCommand.commands.length}`);
        } else {
            console.log(`[parseInputText] Skipped line: '${line}'`);
        }
    };

    lines.forEach(processLine);

    console.log(`[parseInputText] Finished parsing input text.`);
    console.log(`[parseInputText] Total sections in dynamicCmd: ${dynamicCmd.sections.length}`);
    dynamicCmd.sections.forEach((section, index) => {
        console.log(`[parseInputText] Section ${index + 1} has ${section.commands.length} commands`);
    });
    console.log(`[parseInputText] Final parsed dynamicCmd: ${JSON.stringify(dynamicCmd, null, 2)}`);

    return dynamicCmd;
}

// blockExecutor

console.log('[blockExecutor] Starting execution');

const flowContext = flow.get('flowContext') || {};

function helper_waitProcessing(timeout = 30000) {
    console.log('[helper_waitProcessing] Starting to wait for processed result');
    return new Promise((resolve, reject) => {
        const startTime = Date.now();
        const checkProcessedResult = () => {
            const currentContext = flow.get('flowContext') || {};
            if (currentContext.processedResultReady) {
                console.log('[helper_waitProcessing] Processed result is ready');
                currentContext.processedResultReady = false;
                flow.set('flowContext', currentContext);
                resolve(currentContext.processedResult);
            } else if (Date.now() - startTime > timeout) {
                console.warn('[helper_waitProcessing] Timeout reached while waiting for processed result');
                reject(new Error('Timeout waiting for processed result'));
            } else {
                console.log('[helper_waitProcessing] Processed result not ready, checking again in 500ms');
                setTimeout(checkProcessedResult, 500);
            }
        };
        checkProcessedResult();
    });
}

async function executeCommand(command) {
    flow.set('currentCommand', command);
    flow.set('currentResult', '');
    flow.set('triggerDynamicVars', Date.now());
    console.log('[executeCommand] Set currentCommand and triggerDynamicVars in flow');

    const msgToSend = {
        dynamicCmd: true,
        command: command,
        triggerDynamicVars: Date.now()
    };
    console.log('[executeCommand] Sending message:', JSON.stringify(msgToSend));
    node.send(msgToSend);

    return await helper_waitProcessing(60000);
}

async function executeBlockCommands(dynamicCmd) {
    console.log('[executeBlockCommands] Starting execution of commands');

    if (!dynamicCmd || !dynamicCmd.sections || !Array.isArray(dynamicCmd.sections)) {
        console.error("[executeBlockCommands] Invalid dynamicCmd structure:", dynamicCmd);
        throw new Error("Invalid dynamicCmd structure");
    }

    let flowContext = flow.get('flowContext') || {};
    flowContext.previousResponse = flowContext.previousResponse || '[Previous response not available]';
    flow.set('flowContext', flowContext);

    let finalResult = "";

    for (let sectionIndex = 0; sectionIndex < dynamicCmd.sections.length; sectionIndex++) {
        console.log(`[executeBlockCommands] Processing section ${sectionIndex}`);
        const section = dynamicCmd.sections[sectionIndex];

        if (!section || !section.commands || !Array.isArray(section.commands)) {
            console.warn(`[executeBlockCommands] Invalid section structure at index ${sectionIndex}:`, section);
            continue;
        }

        for (let commandIndex = 0; commandIndex < section.commands.length; commandIndex++) {
            console.log(`[executeBlockCommands] Processing command ${commandIndex} in section ${sectionIndex}`);
            const command = section.commands[commandIndex];

            if (!command || !command.type) {
                console.warn(`[executeBlockCommands] Invalid command at section ${sectionIndex}, command ${commandIndex}:`, command);
                continue;
            }

            let result;

            try {
                console.log(`[executeBlockCommands] Executing command of type: ${command.type}`);
                result = await executeCommand(command);

                console.log(`[executeBlockCommands] Command result:`, result);
                command.result = result;
                finalResult += (finalResult ? "
" : "") + result;

                flowContext[`${command.type}${command.number}Result`] = result;
                flow.set('flowContext', flowContext);

                command.processedResult = result;
            } catch (error) {
                console.error(`[executeBlockCommands] Error executing command ${command.type}:`, error);
                command.processedResult = `[Error: ${error.message}]`;
            }

            await new Promise(resolve => setTimeout(resolve, 100));
        }
    }

    // Add the dynamicCmdComplete flag
    msg.dynamicCmdComplete = true;

    return finalResult;
}

function parseIncomingMessage(msg) {
    if (msg.dynamicCmd && !msg.currentCommand) {
        console.log('[parseIncomingMessage] New dynamic command detected');
        return { type: 'newDynamicCmd', data: msg.dynamicCmd };
    } else if (msg.type === 'processedResult') {
        console.log('[parseIncomingMessage] Processed result received');
        return { type: 'processedResult', data: msg.data, command: msg.command };
    } else {
        console.log('[parseIncomingMessage] Pass through message');
        return { type: 'passThrough', data: msg };
    }
}

function handleMessage() {
    console.log('[handleMessage] Starting message handling');
    try {
        const parsedMsg = parseIncomingMessage(msg);

        switch (parsedMsg.type) {
            case 'newDynamicCmd':
                if (!parsedMsg.data) {
                    throw new Error("Invalid dynamicCmd data");
                }
                console.log('[handleMessage] Executing new dynamic command');
                executeBlockCommands(parsedMsg.data)
                    .then(result => {
                        console.log('[handleMessage] Command execution completed. Result:', result);
                        flow.set('finalResult', result);
                        console.log('[handleMessage] Set finalResult in flow');
                        node.send({ payload: result, dynamicCmdComplete: true });
                    })
                    .catch(error => {
                        console.error("[handleMessage] Error processing commands:", error);
                        node.error("Error processing commands: " + error.message, msg);
                    });
                break;

            case 'processedResult':
                console.log('[handleMessage] Setting processed result');
                flow.set('processedResult', parsedMsg.data);
                flow.set('processedResultReady', true);
                console.log('[handleMessage] Updated flow variables: processedResult and processedResultReady');
                break;

            case 'passThrough':
            default:
                console.log('[handleMessage] Passing through message');
                node.send(msg);
                break;
        }
    } catch (error) {
        console.error("[handleMessage] Error in handleMessage:", error);
        node.error("Error in handleMessage: " + error.message, msg);
    }
}

console.log('[blockExecutor] Calling handleMessage');
handleMessage();

console.log('[blockExecutor] Execution completed');
return null;

// commandExecutor

console.log('[commandExecutor] Starting execution');

let flowContext = flow.get('flowContext') || {};

function parseDynamicVariables(text, context) {
    console.log('[parseDynamicVariables] Starting function with text:', text);

    if (typeof text !== 'string') {
        console.warn('[parseDynamicVariables] Warning: text is not a string. Type:', typeof text);
        if (text === null || text === undefined) {
            console.warn('[parseDynamicVariables] Text is null or undefined. Returning empty string.');
            return '';
        }
        try {
            text = String(text);
            console.log('[parseDynamicVariables] Converted text to string:', text);
        } catch (error) {
            console.error('[parseDynamicVariables] Error converting text to string:', error);
            return '[Error: Unable to process non-string input]';
        }
    }

    if (!msg || typeof msg !== 'object') {
        console.warn('[parseDynamicVariables] Warning: msg is not a valid object');
        return text;
    }

    try {
        // Handle previousResponse and sidePromptResult
        if (context && context.latestMsg && typeof context.latestMsg.currentStep === 'number') {
            const currentStep = context.latestMsg.currentStep;
            const previousStep = currentStep - 1;
            const previousResponseKey = `response${previousStep}`;

            if (previousStep >= 0 && context.latestMsg[previousResponseKey]) {
                const previousResponse = context.latestMsg[previousResponseKey];
                console.log(`[parseDynamicVariables] Found previousResponse for step ${previousStep}:`, previousResponse);
                text = text.replace(/{previousResponse}/g, previousResponse);
            } else {
                console.warn(`[parseDynamicVariables] No previousResponse found for step ${previousStep}`);
                text = text.replace(/{previousResponse}/g, '[Previous response not available]');
            }
        } else {
            console.warn('[parseDynamicVariables] Unable to determine previousResponse from context');
            text = text.replace(/{previousResponse}/g, '[Previous response not available]');
        }

        console.log('[parseDynamicVariables] After replacing previousResponse:', text);
        text = text.replace(/{sidePromptResult}/g, context.sidePromptResult || '[Side prompt result not available]');

        // Replace $<<commandTypeNumberResult>>$
        text = text.replace(/\\$<<(\\w+)(\\d+)Result>>\\$/g, (match, cmdType, cmdNumber) => {
            const resultKey = `${cmdType}${cmdNumber}Result`;
            const result = context[resultKey] || `[${resultKey} not available]`;
            console.log(`[parseDynamicVariables] Replaced ${match} with:`, result);
            return result;
        });

        // Replace $<<execute(...)>>$
        text = text.replace(/\\$<<execute\\((.*?)\\)>>\\$/g, (match, code) => {
            console.log(`[parseDynamicVariables] Executing code:`, code);
            try {
                code = code.replace(/;$/, '');
                const safeContext = createSafeContext(msg, context);
                const result = new Function('context', `with(context) { return (${code}); }`)(safeContext);
                console.log(`[parseDynamicVariables] Execution result:`, result);
                return result !== undefined ? result : '[undefined]';
            } catch (error) {
                console.error(`[parseDynamicVariables] Error executing: ${error.message}`);
                return `[Error executing: ${error.message}]`;
            }
        });

        // Handle regular msg property access
        text = text.replace(/\\$<<([\\w.]+)>>\\$/g, (match, path) => {
            console.log(`[parseDynamicVariables] Accessing msg property:`, path);
            const parts = path.split('.');
            let value = msg;
            for (const part of parts) {
                if (value && typeof value === 'object' && part in value) {
                    value = value[part];
                } else {
                    console.warn(`[parseDynamicVariables] Warning: ${path} not available in msg`);
                    return `[${path} not available]`;
                }
            }
            const result = typeof value === 'object' ? JSON.stringify(value) : String(value);
            console.log(`[parseDynamicVariables] Msg property value:`, result);
            return result;
        });

        console.log(`[parseDynamicVariables] Final result: ${text}`);
        return text;
    } catch (error) {
        console.error('[parseDynamicVariables] Unexpected error:', error);
        return `[Error: ${error.message}]`;
    }
}

function createSafeContext(msg, context) {
    return {
        flow: {
            get: (key) => {
                const value = flow.get(key);
                console.log(`[createSafeContext] flow.get('${key}'):`, value);
                return value;
            },
            set: (key, value) => {
                console.log(`[createSafeContext] flow.set('${key}'):`, value);
                flow.set(key, value);
            }
        },
        global: {
            get: (key) => {
                const value = global.get(key);
                console.log(`[createSafeContext] global.get('${key}'):`, value);
                return value;
            },
            set: (key, value) => {
                console.log(`[createSafeContext] global.set('${key}'):`, value);
                global.set(key, value);
            }
        },
        msg: msg,
        context: context
    };
}

function executeCommand(command, context) {
    console.log(`[executeCommand] Executing command:`, JSON.stringify(command));
    let result = '';

    switch (command.type) {
        case 'localCmd':
            result = executeLocalCmd(command, msg, context);
            break;
        case 'compileCmd':
        case 'packCmd':
            result = executeCompileOrPackCmd(command, msg, context);
            break;
        case 'mainPromptCmd':
            result = executeMainPromptCmd(command, msg, context);
            break;
        case 'sidePromptCmd':
            result = executeSidePromptCmd(command, msg, context);
            break;
        default:
            console.warn(`[executeCommand] Unknown command type: ${command.type}`);
            result = `[Unknown command type: ${command.type}]`;
    }

    console.log(`[executeCommand] Result:`, result);
    return result;
}

function executeLocalCmd(command, msg, context) {
    console.log(`[executeLocalCmd] Executing local command:`, JSON.stringify(command));
    let result = '';
    try {
        const commandString = command.commands.join('
');
        console.log(`[executeLocalCmd] Executing: ${commandString}`);
        const safeContext = createSafeContext(msg, context);
        const wrappedCommand = `with(this) { ${commandString} }`;
        const commandResult = new Function(wrappedCommand).call(safeContext);
        result = commandResult !== undefined ? commandResult : '';
    } catch (error) {
        console.error(`[executeLocalCmd] Error: ${error.message}`);
        result = `[Error: ${error.message}]`;
    }
    console.log(`[executeLocalCmd] Result:`, result);
    return result;
}

function executeCompileOrPackCmd(command, msg, context) {
    console.log(`[executeCompileOrPackCmd] Executing compile/pack command:`, JSON.stringify(command));
    let result = '';

    if (command && Array.isArray(command.commands)) {
        result = command.commands.map(cmd => {
            return parseDynamicVariables(cmd, context);
        }).join('
');
    } else {
        console.warn(`[executeCompileOrPackCmd] Invalid command structure:`, JSON.stringify(command));
        result = '[Error: Invalid command structure]';
    }

    console.log(`[executeCompileOrPackCmd] Result:`, result);
    return result.trim();
}

function executeMainPromptCmd(command, msg, context) {
    console.log(`[executeMainPromptCmd] Executing main prompt command:`, JSON.stringify(command));
    const result = command.commands.map(cmd => parseDynamicVariables(cmd, context)).join('
');
    console.log(`[executeMainPromptCmd] Result:`, result);
    return result;
}

async function executeSidePromptCmd(command, msg, context) {
    console.log(`[executeSidePromptCmd] Executing side prompt command:`, JSON.stringify(command));
    const sidePromptCommands = command.commands;

    // Set flow level variable indicating side prompt processing
    flow.set('waitingForSidePrompt', true);

    // Execute side prompt commands
    const result = sidePromptCommands.map(cmd => parseDynamicVariables(cmd, context)).join('
');

    // Send out the message with side prompts
    node.send({ payload: result, sidePrompt: true });

    console.log(`[executeSidePromptCmd] Sent side prompt, waiting for result`);

    try {
        // Wait for the side prompt result
        const sidePromptResult = await waitForSidePromptResult();

        // Update flow context with side prompt result
        flowContext.sidePromptResult = sidePromptResult;
        flow.set('flowContext', flowContext);

        console.log(`[executeSidePromptCmd] Received side prompt result:`, sidePromptResult);
        return sidePromptResult;
    } catch (error) {
        console.error(`[executeSidePromptCmd] Error waiting for side prompt result:`, error);
        return `[Error: ${error.message}]`;
    } finally {
        // Reset the processing flag
        flow.set('waitingForSidePrompt', false);
    }
}

function waitForSidePromptResult(timeout = 100000) {
    console.log('[waitForSidePromptResult] Starting to wait for side prompt result');
    return new Promise((resolve, reject) => {
        const startTime = Date.now();
        const checkSidePromptResult = () => {
            let sidePromptResult = flow.get('sidePromptResult') || {};
            if (flow.get('waitingForSidePrompt') == false) {
                console.log('[waitForSidePromptResult] Side prompt result is ready');
                resolve(sidePromptResult);
            } else if (Date.now() - startTime > timeout) {
                console.warn('[waitForSidePromptResult] Timeout reached while waiting for side prompt result');
                reject(new Error('Timeout waiting for side prompt result'));
            } else {
                console.log('[waitForSidePromptResult] Side prompt result not ready, checking again in 1000ms');
                setTimeout(checkSidePromptResult, 1300);
            }
        };
        checkSidePromptResult();
    });
}


function main() {
    console.log('[main] Starting main function');

    if (!msg.dynamicCmd) {
        console.log('[main] Not a dynamic command message, passing through');
        let outputType = 'normal'; // Default output type
        return {
            ...msg,
            outputType: outputType // Add this line to pass the outputType
        };
    }

    const command = msg.command;
    const triggerDynamicVars = msg.triggerDynamicVars;

    console.log('[main] Received command:', JSON.stringify(command));
    console.log('[main] triggerDynamicVars:', triggerDynamicVars);

    // Refresh flowContext at the beginning of main
    let flowContext = flow.get('flowContext') || {};

    if (command && triggerDynamicVars) {
        console.log('[main] Processing command');
        let finalResult;
        let outputType = 'dynamicCmd'; // Default output type
        console.log('[main] Output type:', outputType);

        try {
            console.log('[main] Executing command:', JSON.stringify(command));
            const executedResult = executeCommand(command, flowContext);
            console.log('[main] Executed result:', executedResult);
            finalResult = executedResult;

            // Set outputType based on command type
            if (command.type === 'sidePromptCmd') {
                outputType = 'sidePrompt';
            } else if (command.type === 'mainPromptCmd') {
                outputType = 'normal';
            }
            // Add more conditions for other command types if needed

        } catch (error) {
            console.error('[main] Error executing command:', error);
            finalResult = `[Error: ${error.message}]`;
            outputType = 'error';
        }

        console.log('[main] Final result:', finalResult);
        console.log('[main] Output type:', outputType);

        flowContext.processedResult = finalResult;
        flowContext.processedResultReady = true;
        flow.set('flowContext', flowContext);

        const resultKey = `${command.type}${command.number}Result`;
        flowContext[resultKey] = finalResult;
        flow.set('flowContext', flowContext);
        console.log(`[main] Stored result for ${resultKey} in flowContext`);

        console.log('[main] Returning processed result');
        return {
            ...msg,
            processedResult: {
                type: 'processedResult',
                data: finalResult,
                command: command
            },
            outputType: outputType // Add this line to pass the outputType
        };
    } else {
        console.log('[main] Invalid command or trigger, passing through message');
        return msg;
    }
}

// The Output Handler Layer

function determineOutputType(msg) {
    console.log('[determineOutputType] Starting output type determination');
    console.log(`[determineOutputType] Input message:`, JSON.stringify(msg));

    if (typeof msg !== 'object' || msg === null) {
        console.warn('[determineOutputType] Invalid message object');
        return 'normal';
    }

    if (msg.outputType) {
        console.log(`[determineOutputType] Output type explicitly set: ${msg.outputType}`);
        return msg.outputType;
    }

    if (msg.processedResult && msg.processedResult.type === 'processedResult') {
        console.log('[determineOutputType] ProcessedResult found');
        const command = msg.processedResult.command;
        if (command && command.type === 'sidePromptCmd') {
            console.log('[determineOutputType] SidePromptCmd detected');
            return 'sidePrompt';
        }
    }

    try {
        const dynamicCmd = flow.get('dynamicCmd');
        console.log('[determineOutputType] DynamicCmd:', JSON.stringify(dynamicCmd));
        if (dynamicCmd && Array.isArray(dynamicCmd.sections) && dynamicCmd.sections.length > 0) {
            const lastSection = dynamicCmd.sections[dynamicCmd.sections.length - 1];
            if (Array.isArray(lastSection.commands) && lastSection.commands.length > 0) {
                const lastCommand = lastSection.commands[lastSection.commands.length - 1];
                console.log(`[determineOutputType] Last command type: ${lastCommand.type}`);
                if (lastCommand.type === 'sidePromptCmd') {
                    return 'sidePrompt';
                }
            }
        }
    } catch (error) {
        console.error('[determineOutputType] Error checking dynamic command:', error);
    }

    if (dynamicCmd === undefined && msg.sidePrompt === true) {
        console.log('[determineOutputType] dynamicCmd is undefined and msg.sidePrompt is true');
        return 'sidePrompt';
    }

    console.log('[determineOutputType] Defaulting to normal output type');
    return 'normal';
}

function handleNormalOutput(msg) {
    console.log('[handleNormalOutput] Handling normal output');
    console.log('[handleNormalOutput] Returning message:', JSON.stringify(msg));
    return [msg, null, null];
}

function handleSidePromptOutput(msg) {
    console.log('[handleSidePromptOutput] Handling side prompt output');
    console.log('[handleSidePromptOutput] Input message:', JSON.stringify(msg));

    let sidePromptContent;
    if (msg.processedResult &&
        msg.processedResult.command &&
        msg.processedResult.command.type === 'sidePromptCmd' &&
        Array.isArray(msg.processedResult.command.commands)) {
        sidePromptContent = msg.processedResult.command.commands;
    } else if (Array.isArray(msg.payload)) {
        sidePromptContent = msg.payload;
    } else {
        sidePromptContent = [msg.payload];
    }

    const sidePromptMsg = {
        ...msg,
        status: "start",
        sidePrompt: true,
        payload: sidePromptContent,
        sidePromptNumber: msg.processedResult.command.number,
    };

    console.log(`[handleSidePromptOutput] Formatted side prompt message:`, JSON.stringify(sidePromptMsg));
    return [null, sidePromptMsg, null];
}

function handleErrorOutput(msg) {
    console.log('[handleErrorOutput] Handling error output');
    console.log('[handleErrorOutput] Error message:', JSON.stringify(msg));
    return [null, null, msg];
}

function garbageCollect() {
    console.log('[garbageCollect] Starting garbage collection');
    const flowKeys = flow.keys();
    flowKeys.forEach(key => {
        flow.set(key, undefined);
        console.log(`[garbageCollect] Cleared flow variable: ${key}`);
    });
    console.log('[garbageCollect] Garbage collection completed');
}

// Input Handler
const flowContext = flow.get('flowContext') || {};

// Update the latest message
flowContext.latestMsg = msg;
flow.set('flowContext', flowContext);

console.log("[inputConditionals] Starting inputConditionals function.");
console.log(`[inputConditionals] Input msg: ${JSON.stringify(msg)}`);

// Check if this is a sidePrompt completion message
if (msg.sidePromptStatus === "done") {
    console.log("[inputConditionals] Received sidePrompt completion message.");
    flow.set('waitingForSidePrompt', false);
    flow.set('sidePromptResult', msg.payload);
    flow.set('flowContext', flowContext);
    return msg; // Send sidePrompt completion to output2
}
// Check if this is a sidePrompt completion message
if (msg.status === "stop") {
    console.log("[inputConditionals] Received stop message.");
    return null; // Send sidePrompt completion to output2
}

// Check if this is a dynamicCmd message
if (msg.payload && typeof msg.payload === 'string' && msg.payload.includes('dynamicCmd')) {
    return msg; // Send dynamicCmd messages to mainProcessor
}

// For all other messages, pass through to output1
return msg;

// The initialParser Layer

function parseInputText(inputText) {
    console.log(`[parseInputText] Starting to parse input text`);
    console.log(`[parseInputText] Input text length: ${inputText.length} characters`);

    const lines = inputText.split('
');
    console.log(`[parseInputText] Number of lines: ${lines.length}`);

    let dynamicCmd = { sections: [] };
    let currentCommand = null;
    let isCapturingNestedCommand = false;
    let nestedCommandBuffer = '';

    console.log(`[parseInputText] Initialized parsing variables`);

    const processLine = (line, index) => {
        console.log(`[parseInputText] Processing line ${index + 1}: '${line}'`);

        const originalLine = line;
        line = line.replace(/\/\/.*$/, '').trim(); // Remove comments

        if (line !== originalLine) {
            console.log(`[parseInputText] Removed comment. New line: '${line}'`);
        }

        if (line === 'dynamicCmd') {
            dynamicCmd.sections.push({ type: 'commandSet', commands: [] });
            console.log(`[parseInputText] Detected 'dynamicCmd' section. Created new command set. Total sections: ${dynamicCmd.sections.length}`);
            return;
        }

        if (line === '- commandSet') {
            console.log(`[parseInputText] Detected commandSet line, ignoring.`);
            return;
        }

        const mainCommandMatch = line.match(/^-\s*(\w+)(\d+)/);
        if (mainCommandMatch) {
            const [, cmdType, cmdNumber] = mainCommandMatch;
            currentCommand = { type: cmdType, number: cmdNumber, commands: [] };
            dynamicCmd.sections[dynamicCmd.sections.length - 1].commands.push(currentCommand);
            console.log(`[parseInputText] Started new main command: ${cmdType}${cmdNumber}. Total commands in current section: ${dynamicCmd.sections[dynamicCmd.sections.length - 1].commands.length}`);
        } else if (line.startsWith('- `')) {
            isCapturingNestedCommand = true;
            nestedCommandBuffer = line.substring(3);
            console.log(`[parseInputText] Started capturing nested command. Initial buffer: '${nestedCommandBuffer}'`);
        } else if (isCapturingNestedCommand) {
            if (line.endsWith('`')) {
                nestedCommandBuffer += line.substring(0, line.length - 1);
                currentCommand.commands.push(nestedCommandBuffer);
                isCapturingNestedCommand = false;
                console.log(`[parseInputText] Finished capturing nested command: '${nestedCommandBuffer}'`);
                console.log(`[parseInputText] Total commands for ${currentCommand.type}${currentCommand.number}: ${currentCommand.commands.length}`);
            } else {
                nestedCommandBuffer += line + '
';
                console.log(`[parseInputText] Capturing nested command. Current buffer length: ${nestedCommandBuffer.length} characters`);
            }
        } else if (line.startsWith('-') && currentCommand) {
            const subCommand = line.substring(1).trim();
            currentCommand.commands.push(subCommand);
            console.log(`[parseInputText] Added sub-command to ${currentCommand.type}${currentCommand.number}: '${subCommand}'`);
            console.log(`[parseInputText] Total commands for ${currentCommand.type}${currentCommand.number}: ${currentCommand.commands.length}`);
        } else {
            console.log(`[parseInputText] Skipped line: '${line}'`);
        }
    };

    lines.forEach(processLine);

    console.log(`[parseInputText] Finished parsing input text.`);
    console.log(`[parseInputText] Total sections in dynamicCmd: ${dynamicCmd.sections.length}`);
    dynamicCmd.sections.forEach((section, index) => {
        console.log(`[parseInputText] Section ${index + 1} has ${section.commands.length} commands`);
    });
    console.log(`[parseInputText] Final parsed dynamicCmd: ${JSON.stringify(dynamicCmd, null, 2)}`);

    return dynamicCmd;
}

