function parseDynamicVariables(text, context) {
    console.log('[parseDynamicVariables] Starting function with text:', text);

    if (typeof text !== 'string') {
        console.warn('[parseDynamicVariables] Warning: text is not a string. Type:', typeof text);
        if (text === null || text === undefined) {
            console.warn('[parseDynamicVariables] Text is null or undefined. Returning empty string.');
            return '';
        }
        try {
            text = String(text);
            console.log('[parseDynamicVariables] Converted text to string:', text);
        } catch (error) {
            console.error('[parseDynamicVariables] Error converting text to string:', error);
            return '[Error: Unable to process non-string input]';
        }
    }

    if (!msg || typeof msg !== 'object') {
        console.warn('[parseDynamicVariables] Warning: msg is not a valid object');
        return text;
    }

    try {
        // Handle previousResponse and sidePromptResult
        if (context && context.latestMsg && typeof context.latestMsg.currentStep === 'number') {
            const currentStep = context.latestMsg.currentStep;
            const previousStep = currentStep - 1;
            const previousResponseKey = `response${previousStep}`;

            if (previousStep >= 0 && context.latestMsg[previousResponseKey]) {
                const previousResponse = context.latestMsg[previousResponseKey];
                console.log(`[parseDynamicVariables] Found previousResponse for step ${previousStep}:`, previousResponse);
                text = text.replace(/{previousResponse}/g, previousResponse);
            } else {
                console.warn(`[parseDynamicVariables] No previousResponse found for step ${previousStep}`);
                text = text.replace(/{previousResponse}/g, '[Previous response not available]');
            }
        } else {
            console.warn('[parseDynamicVariables] Unable to determine previousResponse from context');
            text = text.replace(/{previousResponse}/g, '[Previous response not available]');
        }

        console.log('[parseDynamicVariables] After replacing previousResponse:', text);
        text = text.replace(/{sidePromptResult}/g, context.sidePromptResult || '[Side prompt result not available]');

        // Replace $<<commandTypeNumberResult>>$
        text = text.replace(/\$<<(\w+)(\d+)Result>>\$/g, (match, cmdType, cmdNumber) => {
            const resultKey = `${cmdType}${cmdNumber}Result`;
            const result = context[resultKey] || `[${resultKey} not available]`;
            console.log(`[parseDynamicVariables] Replaced ${match} with:`, result);
            return result;
        });

        // Replace $<<execute(...)>>$
        text = text.replace(/\$<<execute\((.*?)\)>>\$/g, (match, code) => {
            console.log(`[parseDynamicVariables] Executing code:`, code);
            try {
                code = code.replace(/;$/, '');
                const safeContext = createSafeContext(msg, context);
                const result = new Function('context', `with(context) { return (${code}); }`)(safeContext);
                console.log(`[parseDynamicVariables] Execution result:`, result);
                return result !== undefined ? result : '[undefined]';
            } catch (error) {
                console.error(`[parseDynamicVariables] Error executing: ${error.message}`);
                return `[Error executing: ${error.message}]`;
            }
        });

        // Handle regular msg property access
        text = text.replace(/\$<<([\w.]+)>>\$/g, (match, path) => {
            console.log(`[parseDynamicVariables] Accessing msg property:`, path);
            const parts = path.split('.');
            let value = msg;
            for (const part of parts) {
                if (value && typeof value === 'object' && part in value) {
                    value = value[part];
                } else {
                    console.warn(`[parseDynamicVariables] Warning: ${path} not available in msg`);
                    return `[${path} not available]`;
                }
            }
            const result = typeof value === 'object' ? JSON.stringify(value) : String(value);
            console.log(`[parseDynamicVariables] Msg property value:`, result);
            return result;
        });

        console.log(`[parseDynamicVariables] Final result: ${text}`);
        return text;
    } catch (error) {
        console.error('[parseDynamicVariables] Unexpected error:', error);
        return `[Error: ${error.message}]`;
    }
}

function createSafeContext(msg, context) {
    return {
        flow: {
            get: (key) => {
                const value = flow.get(key);
                console.log(`[createSafeContext] flow.get('${key}'):`, value);
                return value;
            },
            set: (key, value) => {
                console.log(`[createSafeContext] flow.set('${key}'):`, value);
                flow.set(key, value);
            }
        },
        global: {
            get: (key) => {
                const value = global.get(key);
                console.log(`[createSafeContext] global.get('${key}'):`, value);
                return value;
            },
            set: (key, value) => {
                console.log(`[createSafeContext] global.set('${key}'):`, value);
                global.set(key, value);
            }
        },
        msg: msg,
        context: context
    };
}

function executeCommand(command, context) {
    console.log(`[executeCommand] Executing command:`, JSON.stringify(command));
    let result = '';

    switch (command.type) {
        case 'localCmd':
            result = executeLocalCmd(command, msg, context);
            break;
        case 'compileCmd':
        case 'packCmd':
            result = executeCompileOrPackCmd(command, msg, context);
            break;
        case 'mainPromptCmd':
            result = executeMainPromptCmd(command, msg, context);
            break;
        case 'sidePromptCmd':
            result = executeSidePromptCmd(command, msg, context);
            break;
        default:
            console.warn(`[executeCommand] Unknown command type: ${command.type}`);
            result = `[Unknown command type: ${command.type}]`;
    }

    console.log(`[executeCommand] Result:`, result);
    return result;
}

function executeLocalCmd(command, msg, context) {
    console.log(`[executeLocalCmd] Executing local command:`, JSON.stringify(command));
    let result = '';
    try {
        const commandString = command.commands.join('
');
        console.log(`[executeLocalCmd] Executing: ${commandString}`);
        const safeContext = createSafeContext(msg, context);
        const wrappedCommand = `with(this) { ${commandString} }`;
        const commandResult = new Function(wrappedCommand).call(safeContext);
        result = commandResult !== undefined ? commandResult : '';
    } catch (error) {
        console.error(`[executeLocalCmd] Error: ${error.message}`);
        result = `[Error: ${error.message}]`;
    }
    console.log(`[executeLocalCmd] Result:`, result);
    return result;
}

function executeCompileOrPackCmd(command, msg, context) {
    console.log(`[executeCompileOrPackCmd] Executing compile/pack command:`, JSON.stringify(command));
    let result = '';

    if (command && Array.isArray(command.commands)) {
        result = command.commands.map(cmd => {
            return parseDynamicVariables(cmd, context);
        }).join('
');
    } else {
        console.warn(`[executeCompileOrPackCmd] Invalid command structure:`, JSON.stringify(command));
        result = '[Error: Invalid command structure]';
    }

    console.log(`[executeCompileOrPackCmd] Result:`, result);
    return result.trim();
}

function executeMainPromptCmd(command, msg, context) {
    console.log(`[executeMainPromptCmd] Executing main prompt command:`, JSON.stringify(command));
    const result = command.commands.map(cmd => parseDynamicVariables(cmd, context)).join('
');
    console.log(`[executeMainPromptCmd] Result:`, result);
    return result;
}

async function executeSidePromptCmd(command, msg, context) {
    console.log(`[executeSidePromptCmd] Executing side prompt command:`, JSON.stringify(command));
    const sidePromptCommands = command.commands;

    // Set flow level variable indicating side prompt processing
    flow.set('waitingForSidePrompt', true);

    // Execute side prompt commands
    const result = sidePromptCommands.map(cmd => parseDynamicVariables(cmd, context)).join('
');

    // Send out the message with side prompts
    node.send({ payload: result, sidePrompt: true });

    console.log(`[executeSidePromptCmd] Sent side prompt, waiting for result`);

    try {
        // Wait for the side prompt result
        const sidePromptResult = await waitForSidePromptResult();

        // Update flow context with side prompt result
        flowContext.sidePromptResult = sidePromptResult;
        flow.set('flowContext', flowContext);

        console.log(`[executeSidePromptCmd] Received side prompt result:`, sidePromptResult);
        return sidePromptResult;
    } catch (error) {
        console.error(`[executeSidePromptCmd] Error waiting for side prompt result:`, error);
        return `[Error: ${error.message}]`;
    } finally {
        // Reset the processing flag
        flow.set('waitingForSidePrompt', false);
    }
}

function waitForSidePromptResult(timeout = 100000) {
    console.log('[waitForSidePromptResult] Starting to wait for side prompt result');
    return new Promise((resolve, reject) => {
        const startTime = Date.now();
        const checkSidePromptResult = () => {
            let sidePromptResult = flow.get('sidePromptResult') || {};
            if (flow.get('waitingForSidePrompt') == false) {
                console.log('[waitForSidePromptResult] Side prompt result is ready');
                resolve(sidePromptResult);
            } else if (Date.now() - startTime > timeout) {
                console.warn('[waitForSidePromptResult] Timeout reached while waiting for side prompt result');
                reject(new Error('Timeout waiting for side prompt result'));
            } else {
                console.log('[waitForSidePromptResult] Side prompt result not ready, checking again in 1000ms');
                setTimeout(checkSidePromptResult, 1300);
            }
        };
        checkSidePromptResult();
    });
}


