function determineOutputType(msg) {
    console.log('[determineOutputType] Starting output type determination');
    console.log(`[determineOutputType] Input message:`, JSON.stringify(msg));

    if (typeof msg !== 'object' || msg === null) {
        console.warn('[determineOutputType] Invalid message object');
        return 'normal';
    }

    if (msg.outputType) {
        console.log(`[determineOutputType] Output type explicitly set: ${msg.outputType}`);
        return msg.outputType;
    }

    if (msg.processedResult && msg.processedResult.type === 'processedResult') {
        console.log('[determineOutputType] ProcessedResult found');
        const command = msg.processedResult.command;
        if (command && command.type === 'sidePromptCmd') {
            console.log('[determineOutputType] SidePromptCmd detected');
            return 'sidePrompt';
        }
    }

    try {
        const dynamicCmd = flow.get('dynamicCmd');
        console.log('[determineOutputType] DynamicCmd:', JSON.stringify(dynamicCmd));
        if (dynamicCmd && Array.isArray(dynamicCmd.sections) && dynamicCmd.sections.length > 0) {
            const lastSection = dynamicCmd.sections[dynamicCmd.sections.length - 1];
            if (Array.isArray(lastSection.commands) && lastSection.commands.length > 0) {
                const lastCommand = lastSection.commands[lastSection.commands.length - 1];
                console.log(`[determineOutputType] Last command type: ${lastCommand.type}`);
                if (lastCommand.type === 'sidePromptCmd') {
                    return 'sidePrompt';
                }
            }
        }
    } catch (error) {
        console.error('[determineOutputType] Error checking dynamic command:', error);
    }

    if (dynamicCmd === undefined && msg.sidePrompt === true) {
        console.log('[determineOutputType] dynamicCmd is undefined and msg.sidePrompt is true');
        return 'sidePrompt';
    }

    console.log('[determineOutputType] Defaulting to normal output type');
    return 'normal';
}

