            main.line[i].Win = 100;
            main.line[i].Takeback = Math.abs(main.line[i].lineValue) + main.line[i].Win;
            main.line[i].Lose = Math.abs(main.line[i].lineValue);
            main.line[i].Vig = Math.abs(main.line[i].lineValue) - main.line[i].Win; 
        } else if (main.line[i].lineValue > 0) {
            main.line[i].Risk = 100;
            main.line[i].Win = main.line[i].lineValue;
            main.line[i].Takeback = main.line[i].Risk + main.line[i].Win;
            main.line[i].Lose = main.line[i].Risk;
            main.line[i].Vig = 0;            
