    main.MoneylineDifference = transform2Message['Bet1'].Line1PointsAmFloat + transform2Message['Bet2'].Line2PointsAmFloat

    main.MoneylineRangeMin = SportsSettingsVar[main.sport].MoneylineRangeMin;
