}

function calculateLineDifference() {
    // Cases : Middle = Loss / Push
    // -120 +140
    // Pay 120 to win 100 / Pay 100 to win 140
    // W1:-$0; L1:+$20 | W2:+$20; L2:$0

    // +100 +120
    // Pay 100 to win 100 / Pay 120 to win 140
    // W1:+$0; L1:+$20 | W2:+$20; L2:+$0

    // -110 -120
    // Pay 110 to win 100 / Pay 120 to win 120
    // W1:-$20; L1:-$10 | W2:-$10; L2:-$20  = DROP
    main.line = {};

    // Iterate over each line
    for (let i = 1; i <= 2; i++) {
        main.line[i] = transform2Message[`Bet${i}`];
        main.line[i].lineValue = transform2Message[`Bet${i}`][`Line${i}PriceAmML`];


        if (main.line[i].lineValue < 0) {
            main.line[i].Risk = Math.abs(main.line[i].lineValue);
            main.line[i].Win = 100;
            main.line[i].Takeback = Math.abs(main.line[i].lineValue) + main.line[i].Win;
            main.line[i].Lose = Math.abs(main.line[i].lineValue);
            main.line[i].Vig = Math.abs(main.line[i].lineValue) - main.line[i].Win;
        } else if (main.line[i].lineValue > 0) {
            main.line[i].Risk = 100;
            main.line[i].Win = main.line[i].lineValue;
            main.line[i].Takeback = main.line[i].Risk + main.line[i].Win;
            main.line[i].Lose = main.line[i].Risk;
            main.line[i].Vig = 0;
        } else if (main.line[i].lineValue === 0) {
            main.line[i].Risk = 100;
            main.line[i].Win = 100;
            main.line[i].Takeback = main.line[i].Risk + main.line[i].Win;
            main.line[i].Lose = main.line[i].Risk;
            main.line[i].Vig = 0;
        }

    }
    main.totalTakeback = main.line[1].Takeback + main.line[2].Takeback;
    return
}

function calculateOutcomes() {
    // Cases : Middle = Loss / Push
    // -120 +140
    // Pay 120 to win 100 / Pay 100 to win 140
    // W1:-$0; L1:+$20 | W2:+$20; L2:$0

    // +100 +120
    // Pay 100 to win 100 / Pay 120 to win 140
    // W1:+$0; L1:+$20 | W2:+$20; L2:+$0

    // -110 -120
    // Pay 110 to win 100 / Pay 120 to win 120
    // W1:-$20; L1:-$10 | W2:-$10; L2:-$20  = DROP

    main.W1 = main.line[1].Takeback - main.line[1].Risk - main.line[2].Risk;
    main.L1 = main.line[2].Takeback - main.line[1].Risk - main.line[2].Risk;
    main.W2 = main.line[2].Takeback - main.line[1].Risk - main.line[2].Risk;
    main.L2 = main.line[1].Takeback - main.line[1].Risk - main.line[2].Risk;
    main.TotalVig = main.line[1].Vig + main.line[2].Vig;
    return
}

function calculateBetType() {
    // Middle / Vigsplit / Arb
    // is it a good middle?
    // is it a + vigsplit and - spread?
    // is it a + 5% arb with low outlay?

    // TO DO : 
    // 1. Calculate Arb
    let risk = 100;
    main.arbResults = calculateArbitrage(main.line[1].lineValue, main.line[2].lineValue, risk);

    // 2. Calculate VigSplit
    main.line1VSP = main.line[1].Win - main.line[1].Risk;
    main.line2VSP = main.line[2].Win - main.line[2].Risk;
    main.vigSplitProfit = main.line1VSP + main.line2VSP;
    main.vigSplitProfitRate = main.vigSplitProfit / (main.line[1].Risk + main.line[2].Risk)
    main.vigSplitTotalRisk = main.line[1].Risk + main.line[2].Risk;

    // A: If high middle, take as middle
    if (main.MoneylineDifference >= main.MoneylineRangeMin
        && main.TotalVig <= vigMax) {
        main.BetType = "MONEYLINEBET";
    }
    // B: If Arb Less than Range min & greater than 0
    else if (main.MoneylineDifference < main.MoneylineRangeMin
        && main.arbResults.ArbPercentage >= .05
        && main.arbResults.ArbPercentage <= .15) {
        main.BetType = "ARBBET";
        //Take it.
    }
    // C: VIGSPLIT LAST RESORT : If less then Range Min & less than 5% Arb & more than 15
    else if (main.MoneylineDifference < main.MoneylineRangeMin
        && main.arbResults.ArbPercentage < .05 && main.arbResults.vigSplitProfit > 15) {
        main.BetType = "VIGSPLITBET";
        //Take it.
    }
    else {
        main.BetType = false;
    }

    return
}

function calculateStake(globalStake) {

    main.arbResults = calculateArbitrage(main.line[1].lineValue, main.line[2].lineValue, globalStake);
    if (main.BetType == "MIDDLEBET") {
        main.line[1].stake = globalStake;
        main.line[2].stake = globalStake;
    }
    else if (main.BetType == "ARBITRAGEBET") {
        main.line[1].stake = (main.arbResults.ArbBetOnOutcome1 / 100) * globalStake;
        main.line[2].stake = (main.arbResults.ArbBetOnOutcome2 / 100) * globalStake;
    }
    if (main.BetType == "VIGSPLITBET") {
        main.line[1].stake = (main.line[1].risk / 100) * globalStake;
        main.line[2].stake = (main.line[2].risk / 100) * globalStake;
    }
}
