    flow.set('flowContext', flowContext);  // This line adds flow-level persistence
    console.log(`[initialParser] Stored parsed dynamicCmd in flow context`);

    msg.dynamicCmdContext = parsedCmd;

    // Initialize other contexts if they don't exist
    msg.inputContext = msg.inputContext || { filePrompt: "", filePath: "" };
    msg.promptSectionContext = msg.promptSectionContext || {
        promptSectionsArray: [],
        status: "",
        _continuation: false,
        _event: "",
        currentStep: 0,
        totalSteps: 0,
        promptConfig: { title: "", body: "" }
    };

    console.log(`[initialParser] Attached parsed dynamicCmdContext to msg object`);
    node.send([null, msg]);
} else {
    console.log(`[initialParser] Received non-dynamicCmd message, passing through`);
    msg.dynamicCmdContext = { dynamicCmd: false };
    node.send([msg, null]);
}

console.log('[blockExecutor] Starting execution');

const flowContext = flow.get('flowContext') || {};

/**
 * Waits for a processed result to be ready in the flow context.
 * @param {number} timeout - Maximum time to wait in milliseconds
 * @returns {Promise<any>} The processed result
 */
async function waitForProcessedResult(timeout = 600000) {
    console.log('[waitForProcessedResult] Waiting for processed result');
    const startTime = Date.now();

    function checkResultReady() {
        const flowContext = flow.get('flowContext') || {};
        if (flowContext.processedResultReady) {
            console.log('[waitForProcessedResult] Result ready');
            flowContext.processedResultReady = false;
            flow.set('flowContext', flowContext);
            return flowContext.processedResult;
        }
        return null;
    }

    function checkTimeout() {
        if (Date.now() - startTime > timeout) {
            throw new Error('Timeout waiting for processed result');
        }
    }

    async function waitBeforeNextCheck() {
        await new Promise(resolve => setTimeout(resolve, 500));
    }

    async function waitLoop() {
        while (true) {
            const result = checkResultReady();
            if (result !== null) {
                return result;
            }
            checkTimeout();
            await waitBeforeNextCheck();
        }
    }

    return await waitLoop();
}

/**
 * Executes a single command and waits for its result.
 * @param {Object} command - The command to execute
 * @returns {Promise<any>} The result of the command execution
 */
async function executeCommand(command) {
    node.send([null, {
        ...msg,
        dynamicCmdContext: {
            dynamicCmd: true,
            command: command,
            triggerCommandExecution: Date.now()
        }
        }]);

    return await waitForProcessedResult(600000);
}

/**
 * Executes all commands in a dynamicCmd structure.
 * @param {Object} dynamicCmdContext - The dynamicCmd context containing commands to execute
 * @returns {Promise<Array>} An array of results from all command executions
 */
async function executeBlockCommands(dynamicCmdContext) {
    function initializeFlowContext() {
        let flowContext = flow.get('flowContext') || {};
        flowContext.previousResponse = flowContext.previousResponse || '[Previous response not available]';
        flow.set('flowContext', flowContext);
        console.log("[executeBlockCommands] flowContext", flowContext)
        return flowContext;
    }

    async function executeSingleCommand(command, flowContext, index, commandsArray) {
        if (!command?.type) return null;

        // Update dynamicCmdContext with current command info
        dynamicCmdContext.currentCommand = {
            type: command.type,
            number: command.number,
            index: index + 1,
            total: commandsArray.length
        };

        console.log(`[executeSingleCommand] Processing command ${index + 1}/${commandsArray.length}: ${command.type}${command.number}`);

        try {
            const result = await executeCommand(command);
            command.result = result;

            // Store the result in the DynamicCmdContext.command structure
            dynamicCmdContext.command = {
                type: command.type,
                number: command.number,
                result: result
            };

            //console.log("[executeSingleCommand] Flow Context: ", JSON.stringify(flowContext, null, 2));
            console.log("[executeSingleCommand] Command: ", JSON.stringify(command, null, 2));
            // Find the command in the array and update its result
            const commandIndex = flowContext.dynamicCmdContext.commandsArray.findIndex(
            cmd => cmd.type === command.type && cmd.number === command.number
            );

            // Log the updated dynamicCmdContext after each command
            flowContext.dynamicCmdContext.commandsArray[commandIndex].result = result;
            flow.set('flowContext', flowContext);
            msg.dynamicCmdContext = flowContext.dynamicCmdContext;
            console.log(`[executeSingleCommand] Updated dynamicCmdContext:`, JSON.stringify(msg.dynamicCmdContext, null, 2));

            return result;
        } catch (error) {
            console.error(`[executeBlockCommands] Error executing ${command.type}:`, error);
            command.processedResult = `[Error: ${error.message}]`;
            return `[Error: ${error.message}]`;
        }
    }

    async function executeBlockCommandMain() {
        if (!dynamicCmdContext?.commandsArray?.length) {
            throw new Error("Invalid dynamicCmd structure");
        }

        let flowContext = initializeFlowContext();
        let results = [];
        const commandsArray = flowContext.dynamicCmdContext.commandsArray;

        for (let i = 0; i < commandsArray.length; i++) {
            const command = commandsArray[i];
            const result = await executeSingleCommand(command, flowContext, i, commandsArray);
            results.push(result);

            // Log the progress after each command
            console.log(`[executeBlockCommandMain] Completed command ${i + 1}/${commandsArray.length}`);

            await new Promise(resolve => setTimeout(resolve, 100));

            // Check if this is the last command
            if (i === commandsArray.length - 1) {
                msg.dynamicCmdContext.blockComplete = true;
                console.log('[executeBlockCommandMain] All commands processed, blockComplete set to true');
            }
        }

        // Clear current command info after all commands are processed
        dynamicCmdContext.currentCommand = null;

        // Log the final dynamicCmdContext
        console.log(`[executeBlockCommandMain] Final dynamicCmdContext:`, JSON.stringify(msg.dynamicCmdContext, null, 2));

        return results;
    }

    return await executeBlockCommandMain();
}

/**
 * Handles the execution of a dynamic command.
 * @param {Object} data - The dynamic command data
 */
async function handleDynamicCommand(data) {
    if (!data || !data.commandsArray) throw new Error("Invalid dynamicCmd data");

    console.log('[handleDynamicCommand] Executing new dynamic command');
    try {
        const results = await executeBlockCommands(data);

        msg.dynamicCmdContext.results = results;
        msg.dynamicCmdContext.finalResult = results[results.length - 1]; // Set the last result as the final result

        if (msg.dynamicCmdContext.blockComplete) {
            console.log('[handleDynamicCommand] All commands processed successfully');
            console.log('[handleDynamicCommand] result : ', msg.dynamicCmdContext.finalResult);
            node.send([msg, null]);
        } else {
            console.log('[handleDynamicCommand] Not all commands processed, check for errors');
        }
    } catch (error) {
        console.error("[handleDynamicCommand] Error processing commands:", error);
        node.error("Error processing commands: " + error.message, msg);
    }
}

/**
 * Parses the incoming message and categorizes it.
 * @param {Object} msg - The incoming message
 * @returns {Object} Categorized message object
 */
function parseIncomingMessage(msg) {
    if (msg.dynamicCmdContext && msg.dynamicCmdContext.dynamicCmd) {
        console.log('[parseIncomingMessage] New dynamic command detected');
        return { type: 'dynamicCmd', data: msg.dynamicCmdContext };
    } else {
        console.log('[parseIncomingMessage] Pass through message');
        return { type: 'passThrough', data: msg };
    }
}

/**
 * Handles different types of messages based on their category.
 */
async function main() {
    console.log('[main] Starting message handling');
    try {
        const parsedMsg = parseIncomingMessage(msg);

        switch (parsedMsg.type) {
            case 'dynamicCmd':
                await handleDynamicCommand(parsedMsg.data);
                break;
        }
    } catch (error) {
        console.error("[main] Error:", error);
        node.error("Error in main: " + error.message, msg);
    }
}

