function cleanup(browser, result, msg) {
  if (browser) {
    browser.close().then(() => console.log('[Run Prompt Side] Browser closed'));
  }

  node.status(result.status);

  let isLastMessage = result.endOfSequence || false;

  const firstOutput = {
    ...msg,
    endOfSequence: isLastMessage,
    sidePromptContext: {
      ...msg.sidePromptContext,
      _continuation: !isLastMessage,
      status: result.status.text,
      responseArray: [
          ...nodeContext.sidePromptContext.responseArray,
      ],
      _event: isLastMessage ? "End of Sequence" : "Continue",
      sidePromptCmdDone: false
    },
  };

   return firstOutput;
}

async function executeScript(msg) {
