      console.log('last step result: ', result);
      context.set('nodeContext', undefined);
       node.send([result, null]);
    }
    else {
        let sendMsg = cleanup(browser, result, msg);
        node.send([sendMsg, null]);
    }
  } catch (error) {
    result = handleError(error);
  }


}

