function helper_waitProcessing(timeout = 30000) {
    console.log('[helper_waitProcessing] Starting to wait for processed result');
    return new Promise((resolve, reject) => {
        const startTime = Date.now();
        const checkProcessedResult = () => {
            const currentContext = flow.get('flowContext') || {};
            if (currentContext.processedResultReady) {
                console.log('[helper_waitProcessing] Processed result is ready');
                currentContext.processedResultReady = false;
                flow.set('flowContext', currentContext);
                resolve(currentContext.processedResult);
            } else if (Date.now() - startTime > timeout) {
                console.warn('[helper_waitProcessing] Timeout reached while waiting for processed result');
                reject(new Error('Timeout waiting for processed result'));
            } else {
                console.log('[helper_waitProcessing] Processed result not ready, checking again in 500ms');
                setTimeout(checkProcessedResult, 500);
            }
        };
        checkProcessedResult();
    });
}

