} else {
    node.error('Invalid payload type. Expected an array or a comma-separated string.');
    return null;
}

node.warn('filepaths : ' + filePaths.length);

const debounceTimeout = 500; // Adjust this value as needed (in milliseconds)
const fileChangeTimers = {};

filePaths.forEach(path => {
    if (fs.existsSync(path)) {
        fs.watch(path, (eventType, filename) => {
            if (filename) {
                clearTimeout(fileChangeTimers[path]); // Clear any previous timer

                fileChangeTimers[path] = setTimeout(() => {
                    const changeMsg = {
                        payload: path,
                        filename: filename,
                        topic: path,
                        file: filename,
                        type: eventType,
                        size: fs.statSync(path).size
                    };
                    node.send(changeMsg);
                }, debounceTimeout);
            }
        });
