// Disable the watch node to prevent recursive triggers
flow.set('disableWatch', true);

// Retrieve file content from the message payload
const fileContent = msg.payload;
node.warn('File content length: ' + fileContent.length);

// Parse the file content to generate an AST
let ast;
try {
    ast = acorn.parse(fileContent, { ecmaVersion: 2020, sourceType: "module" });
} catch (e) {
    node.error('Error parsing the file content: ' + e.message);
    return null;
}

// Helper function to get the function name from a node
function getFunctionName(node) {
    return node.id ? node.id.name : (node.key ? node.name : null);
}

// Extract all top-level async functions
const topLevelAsyncFunctions = [];
ast.body.forEach(node => {
    if (node.type === 'FunctionDeclaration' && node.async) {
        topLevelAsyncFunctions.push(node);
    } else if (node.type === 'VariableDeclaration') {
        node.declarations.forEach(declaration => {
            if (declaration.init && declaration.init.type === 'FunctionExpression' && declaration.init.async) {
                topLevelAsyncFunctions.push(declaration.init);
            }
        });
    }
});

if (topLevelAsyncFunctions.length === 0) {
    node.error('No top-level async functions found.');
    return null;
}

let newFileContent = fileContent;
let changesMade = false;

// First, gather the inner functions for each top-level async function
const functionData = topLevelAsyncFunctions.map(funcNode => {
    const functionName = getFunctionName(funcNode);
    if (!functionName) return null;

    node.warn('Function found: ' + functionName);

    // Extract top-level inner function names
    const innerFunctionNames = [];
    const innerFunctionRegex = /async function (\w+)\s*\(.*?\)\s*{/g;
    let functionBody = fileContent.slice(funcNode.body.start, funcNode.body.end);
    let innerFunctionMatch;
    while ((innerFunctionMatch = innerFunctionRegex.exec(functionBody)) !== null) {
        // Ensure that the function is directly within the top-level function and not named "execute" or "default"
        const functionStartIndex = innerFunctionMatch.index;
        const precedingText = functionBody.slice(0, functionStartIndex);
        const openBraces = (precedingText.match(/{/g) || []).length;
        const closeBraces = (precedingText.match(/}/g) || []).length;
        const innerFunctionName = innerFunctionMatch[1];
        if (openBraces === closeBraces + 1 && innerFunctionName !== "execute" && innerFunctionName !== "default") {
            innerFunctionNames.push(innerFunctionName);
        }
    }
    node.warn('Top-level inner function names: ' + innerFunctionNames.join(', '));

    return { funcNode, innerFunctionNames };
}).filter(Boolean);

// Helper function to insert the innerFunctions object
function insertInnerFunctions(funcNode, innerFunctionNames, offset) {
    if (innerFunctionNames.length === 0) {
        return 0; // No inner functions to insert
    }

    // Check if 'innerFunctions' is already declared within the function body
    const functionBody = fileContent.slice(funcNode.body.start + offset, funcNode.body.end + offset);
    const innerFunctionsDeclared = /const innerFunctions = {([^}]*)}/.exec(functionBody);

    if (innerFunctionsDeclared) {
        const existingInnerFunctions = innerFunctionsDeclared[1];
        const existingFunctions = existingInnerFunctions.split(',').map(fn => fn.trim()).filter(fn => fn.length > 0);
        const missingFunctions = innerFunctionNames.filter(fn => !existingFunctions.includes(fn));
        if (missingFunctions.length === 0) {
            node.warn(`All inner functions already declared in ${getFunctionName(funcNode)}, skipping insertion.`);
            return 0; // No adjustment needed
