}

function calculateBetType() {
    // Middle / Vigsplit / Arb
    // is it a good middle?
    // is it a + vigsplit and - spread?
    // is it a + 5% arb with low outlay?

    // TO DO : 
    // 1. Calculate Arb
    let risk = 100;
    main.arbResults = calculateArbitrage(main.line[1].lineValue, main.line[2].lineValue, risk);

    // 2. Calculate VigSplit
    main.line1VSP = main.line[1].Win - main.line[1].Risk;
    main.line2VSP = main.line[2].Win - main.line[2].Risk;
    main.vigSplitProfit = main.line1VSP + main.line2VSP;
    main.vigSplitProfitRate = main.vigSplitProfit / (main.line[1].Risk + main.line[2].Risk)
    main.vigSplitTotalRisk = main.line[1].Risk + main.line[2].Risk;

    // A: If high middle, take as middle
    if (main.PointspreadPointsDifference >= main.PointspreadRangeMin
        && main.TotalVig <= vigMax) {
        main.BetType = "MIDDLEBET";
    }   
    // B: If Arb Less than Range min & greater than 0
    else if (main.PointspreadPointsDifference < main.PointspreadRangeMin
        && main.arbResults.ArbPercentage >= .05
        && main.arbResults.ArbPercentage <= .15){
        main.BetType = "ARBBET";
        //Take it.
