    // Extract the message ID from the incoming message
    var betIDVar = incomingMessage.fullMessage._id;

    // Ensure buffer entry exists for the current message ID and initialize it as an empty array if it doesn't
    if (!buffer[betIDVar]) {
        buffer[betIDVar] = {};
        buffer[betIDVar].messages = [];
    }

    // Add the incoming message to the buffer array for this betIDVar
    buffer[betIDVar].messages.push(incomingMessage);

