                return;
            }

            console.log('[Chrome Starter] Chrome Canary processes closed, waiting briefly for processes to end');
            setTimeout(checkChromeProcesses, 2000); // Wait 2 seconds before checking
        });
    } else if (osType === 'mac' || osType === 'linux') {
        // Use pgrep to find the process ID and then send a quit signal
        childProcess.exec(`pgrep -f "${chromePath}"`, (error, stdout, stderr) => {
            if (error) {
                console.error('[Chrome Starter] Error finding Chrome Canary processes:', stderr || error);
                forceKillChrome();
                return;
            }

            const pids = stdout.trim().split('\n').filter(pid => pid.trim() !== '');
            if (pids.length > 0) {
                console.log(`[Chrome Starter] Found Chrome Canary process(es): ${pids.join(', ')}. Attempting to gracefully quit each...`);
                pids.forEach(pid => {
                    childProcess.exec(`kill -15 ${pid}`, (error, stdout, stderr) => {
                        if (error) {
                            console.error(`[Chrome Starter] Error sending quit signal to PID ${pid}:`, stderr || error);
                        } else {
                            console.log(`[Chrome Starter] Quit signal sent to PID ${pid}.`);
                        }
                    });
                });

                // Delay to allow processes to quit gracefully
                setTimeout(checkChromeProcesses, 5000); // 5 seconds before checking
            } else {
                console.log('[Chrome Starter] No Chrome Canary process found to quit');
                sendOutput({
                    payload: "No Chrome Canary process found to quit",
                    chromeEnded: true,
                    error: null,
                    errorMessage: null
                }, "yellow", "No Chrome Canary process found");
            }
        });
    } else {
        console.error('[Chrome Starter] Unsupported OS detected');
    }
}


function checkChromeProcesses() {
    const osType = getOperatingSystem();

    if (osType === 'windows') {
        childProcess.exec('tasklist /FI "IMAGENAME eq chrome.exe" /FO CSV /NH /FI "WINDOWTITLE eq Google Chrome Canary"', (error, stdout, stderr) => {
            if (error) {
                console.error('[Chrome Starter] Error checking Chrome Canary processes:', error);
                forceKillChrome();
                return;
            }

            const chromeProcesses = stdout.split('\n').filter(line => line.includes('chrome.exe'));

            if (chromeProcesses.length === 0) {
                console.log('[Chrome Starter] All Chrome Canary processes ended successfully');
                context.set('chromeRunning', false, 'file');
                sendOutput({
                    payload: "Chrome Canary processes ended successfully",
                    chromeEnded: true,
                    error: null,
                    errorMessage: null
                }, "blue", "Chrome Canary ended");
            } else {
                console.log('[Chrome Starter] Chrome Canary processes still running, force killing');
                forceKillChrome();
            }
        });
    } else if (osType === 'mac' || osType === 'linux') {
        childProcess.exec('pgrep -f "Google Chrome Canary"', (error, stdout, stderr) => {
            if (error) {
                console.error('[Chrome Starter] Error checking Chrome Canary processes:', error);
                forceKillChrome();
                return;
            }

            const chromeProcesses = stdout.split('\n').filter(line => line.trim() !== '');

            if (chromeProcesses.length === 0) {
                console.log('[Chrome Starter] All Chrome Canary processes ended successfully');
                context.set('chromeRunning', false, 'file');
                sendOutput({
                    payload: "Chrome Canary processes ended successfully",
                    chromeEnded: true,
                    error: null,
                    errorMessage: null
                }, "blue", "Chrome Canary ended");
            } else {
                console.log('[Chrome Starter] Chrome Canary processes still running, force killing');
                forceKillChrome();
            }
        });
    } else {
        console.error('Unsupported OS');
    }
}

function forceKillChrome() {
    const osType = getOperatingSystem();

    console.log(`[Chrome Starter] Detected OS: ${osType}`);

    if (osType === 'windows') {
        console.log('[Chrome Starter] Attempting to force kill Chrome Canary on Windows using taskkill...');
        childProcess.exec('taskkill /F /IM chrome.exe /FI "WINDOWTITLE eq Google Chrome Canary"', (error, stdout, stderr) => {
            if (error) {
                console.error('[Chrome Starter] Error force killing Chrome Canary process:', error);
                console.error('[Chrome Starter] Command output:', stdout);
                console.error('[Chrome Starter] Command error output:', stderr);
                sendOutput({
                    payload: "Error ending Chrome Canary process",
                    chromeEnded: false,
                    error: error,
                    errorMessage: error.message || JSON.stringify(error)
                }, "red", "Error ending Chrome Canary");
            } else {
                console.log('[Chrome Starter] Chrome Canary process force killed successfully. Command output:', stdout);
                context.set('chromeRunning', false, 'file');
                sendOutput({
                    payload: "Chrome Canary process force killed successfully",
                    chromeEnded: true,
                    error: null,
                    errorMessage: null
                }, "blue", "Chrome Canary ended (forced)");
            }
        });
    } else if (osType === 'mac' || osType === 'linux') {
        console.log(`[Chrome Starter] Attempting to find Chrome Canary processes on ${osType} using pgrep...`);
        childProcess.exec('pgrep -f "Google Chrome Canary"', (error, stdout, stderr) => {
            console.log('[Chrome Starter] pgrep command output:', stdout);
            console.log('[Chrome Starter] pgrep command error output:', stderr);
            const pids = stdout.trim().split('\n');

            if (pids.length === 0 || pids[0] === '') {
                console.log('[Chrome Starter] No Chrome Canary process found to kill');
                sendOutput({
                    payload: "No Chrome Canary process found to kill",
                    chromeEnded: true,
                    error: null,
                    errorMessage: null
                }, "yellow", "No Chrome Canary process found");
            } else {
                console.log(`[Chrome Starter] Found Chrome Canary process(es): ${pids.join(', ')}. Attempting to force kill each...`);

                let killErrors = [];
                let killSuccess = false;

                pids.forEach(pid => {
                    console.log(`[Chrome Starter] Attempting to kill process ID: ${pid}`);
                    childProcess.exec(`kill -9 ${pid}`, (error, stdout, stderr) => {
                        if (error) {
                            console.error(`[Chrome Starter] Error force killing Chrome Canary process with PID ${pid}:`, error);
                            console.error(`[Chrome Starter] kill command output for PID ${pid}:`, stdout);
                            console.error(`[Chrome Starter] kill command error output for PID ${pid}:`, stderr);
                            killErrors.push(`Error killing PID ${pid}: ${error.message}`);
                        } else {
                            console.log(`[Chrome Starter] Successfully killed Chrome Canary process with PID ${pid}.`);
                            killSuccess = true;
                        }
                    });
                });

                setTimeout(() => {
                    if (killSuccess) {
                        context.set('chromeRunning', false, 'file');
                        sendOutput({
                            payload: "Chrome Canary process(es) force killed successfully",
                            chromeEnded: true,
                            error: null,
                            errorMessage: null
                        }, "blue", "Chrome Canary ended (forced)");
                    } else {
                        sendOutput({
                            payload: "Failed to end some or all Chrome Canary processes",
                            chromeEnded: false,
                            error: new Error(killErrors.join('\n')),
                            errorMessage: killErrors.join('\n')
                        }, "red", "Error ending Chrome Canary");
                    }
                }, 1000);  // Delay to allow all kill commands to complete
            }
        });
    } else {
        console.error('[Chrome Starter] Unsupported OS detected');
    }
}

function restartChromeProcess() {
    console.log('[Chrome Starter] Restart process initiated');

    if (!context.get('chromeRunning')) {
        console.log('[Chrome Starter] Chrome Canary is not running. Starting new instance.');
        node.status({ fill: "blue", shape: "dot", text: "Starting Chrome..." });
        startChrome();
        return;
    }

    console.log('[Chrome Starter] Restarting Chrome Canary process');
    node.status({ fill: "blue", shape: "dot", text: "Restarting Chrome Canary..."});

    // First, end the Chrome Canary process
    endChromeProcess(chromePath);

    // Wait for the end process to complete before starting
    const checkAndStart = setInterval(() => {
        if (!context.get('chromeRunning')) {
            clearInterval(checkAndStart);
            console.log('[Chrome Starter] Chrome Canary process ended, now starting...');
            setTimeout(startChrome, 1000); // Wait an additional second before starting
        }
    }, 500); // Check every 500ms

    // Set a timeout to prevent infinite waiting
    setTimeout(() => {
        clearInterval(checkAndStart);
        if (context.get('chromeRunning')) {
            console.log('[Chrome Starter] Restart failed: Could not end Chrome Canary process');
            sendOutput({
                payload: "Restart failed: Could not end Chrome Canary process",
                chromeRestarted: false,
                error: new Error("Could not end Chrome Canary process during restart"),
                errorMessage: "Could not end Chrome Canary process during restart"
            }, "red", "Restart failed");
        }
    }, 30000); // 30 second timeout
}

