// Define the ChromeController class
class ChromeController {
    constructor() {
        this.browser = null;
        this.isReady = false;
        this.lastNavigatedUrl = null;  // Add this line
    }

    async connectToBrowser() {
        console.log('Attempting to connect to Chrome via CDP on port 27000...');
        this.browser = await playwright.chromium.connectOverCDP('http://localhost:27000');
        console.log('Successfully connected to Chrome via CDP on port 27000.');
        this.isReady = true;
    }

    async logContextsAndPages() {
        const contexts = this.browser.contexts();
        console.log(`Found ${contexts.length} context(s).`);

        for (let i = 0; i < contexts.length; i++) {
            const context = contexts[i];
            const pages = context.pages();
            console.log(`Context ${i + 1} has ${pages.length} page(s).`);
            for (let j = 0; j < pages.length; j++) {
                console.log(`  Page ${j + 1}: ${pages[j].url()}`);
            }
        }

        return contexts;
    }

    async getOrCreateContext() {
        let contexts = this.browser.contexts();
        if (contexts.length === 0) {
            console.log('No contexts found, creating a new one.');
            return await this.browser.newContext();
        }
        return contexts[0];
    }

    async getOrCreatePage(context) {
        const pages = context.pages();
        if (pages.length > 0) {
            console.log('Found an open tab, using it.');
            return pages[0];
        } else {
            console.log('No open tabs found, creating a new tab.');
            return await context.newPage();
        }
    }

    async navigateToChat(page) {
        console.log('Navigating to ChatGPT...');
        await page.goto('https://chat.openai.com', { waitUntil: 'networkidle' });
        console.log('Navigated to ChatGPT using the connected Chrome instance.');
        const title = await page.title();
        console.log(`Page title after navigation: ${title}`);
        this.lastNavigatedUrl = 'https://chat.openai.com';  // Add this line
    }


    async disconnectFromBrowser() {
        if (this.browser) {
            try {
                await this.browser.close();
                console.log('Closed the CDP connection to the browser.');
            } catch (error) {
                console.error('Error closing CDP connection:', error);
            } finally {
                this.browser = null;
                this.isReady = false;
            }
        }
    }

    async handleMessage(message) {
        console.log('Handling message:', JSON.stringify(message));
        try {
            if (typeof message === 'object' && message.chromeStarted === true) {
                console.log('Chrome started. Connecting to the browser...');
                await this.connectToBrowser();
                await this.logContextsAndPages();
                const context = await this.getOrCreateContext();
                const page = await this.getOrCreatePage(context);
                await this.navigateToChat(page);
            } else if (typeof message === 'object' && message.chromeEnded === true) {
                console.log('Chrome ended. Disconnecting from the browser...');
                await this.disconnectFromBrowser();
            } else {
                console.log('Unrecognized message. Ignoring.');
            }
        } catch (error) {
            console.error('Error handling message:', error);
        }
    }
}


