// Function to detect the operating system
function getOperatingSystem() {
    const osPlatform = os.platform();
    if (osPlatform === 'win32') {
        return 'windows';
    } else if (osPlatform === 'darwin') {
        return 'mac';
    } else if (osPlatform === 'linux') {
        return 'linux';
    } else {
        console.error('Unsupported OS');
        return 'unsupported';
    }
}

let chromePath = global.get('userRootPaths').chrome;

const chromeArgs = ['--remote-debugging-port=27000', '--profile-directory=Profile 4'];

// Initialize context variables if they don't exist
if (context.get('chromeRunning') === undefined) {
    context.set('chromeRunning', false, 'file');
}
if (context.get('checkAttempts') === undefined) {
    context.set('checkAttempts', 0, 'file');
}

const maxAttempts = 10;

function sendOutput(msg, fillColor, text) {
    node.status({ fill: fillColor, shape: "dot", text: text });
    console.log('[Chrome Starter] Sending output message:', msg);
    node.send(msg);
}

function checkChromeRunning() {
    let command;
    let processName;
    const osType = getOperatingSystem();

    console.log(`[checkChromeRunning] Detected OS: ${osType}`);

    if (osType === 'windows') {
        // Simplified to only check if Chrome is running
        command = 'tasklist /FI "IMAGENAME eq chrome.exe"';
        processName = 'chrome.exe';
    } else if (osType === 'mac') {
        command = 'ps aux | grep "[G]oogle Chrome Canary"';
        processName = 'Google Chrome Canary';
    } else if (osType === 'linux') {
        command = 'ps aux | grep "[C]hrome"';
        processName = 'chrome';
    } else {
        console.error('[checkChromeRunning] Unsupported OS');
        return;
    }

    console.log(`[checkChromeRunning] Executing command: ${command}`);

    childProcess.exec(command, (error, stdout, stderr) => {
        let checkAttempts = context.get('checkAttempts') || 0;

        console.log(`[checkChromeRunning] Command output: ${stdout}`);
        console.log(`[checkChromeRunning] Command error output (if any): ${stderr}`);

        if (error) {
            console.error(`[checkChromeRunning] Command execution error: ${error.message}`);
        }

        if (stdout.toLowerCase().includes(processName.toLowerCase())) {
            console.log('[Chrome Starter] Chrome Canary process found running');
            context.set('chromeRunning', true, 'file');
            context.set('checkAttempts', 0, 'file');
            sendOutput({
                payload: "Chrome started successfully (verified running)",
                chromeStarted: true,
                error: null,
                errorMessage: null
            }, "green", "Chrome started (verified)");
        } else {
            console.log(`[Chrome Starter] Chrome Canary process not found, attempt ${checkAttempts + 1}`);
            checkAttempts++;
            context.set('checkAttempts', checkAttempts, 'file');
            if (checkAttempts >= maxAttempts) {
                console.log('[Chrome Starter] Max attempts reached, Chrome start failed');
                context.set('chromeRunning', false, 'file');
                context.set('checkAttempts', 0, 'file');
                sendOutput({
                    payload: "Chrome failed to start (process not found after multiple attempts)",
                    chromeStarted: false,
                    error: new Error("Chrome process not found after multiple attempts"),
                    errorMessage: "Chrome process not found after multiple attempts"
                }, "red", "Chrome start failed");
            } else {
                setTimeout(checkChromeRunning, 2000);
            }
        }
    });
}



function startChrome() {
    if (context.get('chromeRunning')) {
        console.log('[Chrome Starter] Chrome is already running');
        sendOutput({
            payload: "Chrome is already running",
            chromeStarted: true,
            error: null,
            errorMessage: null
        }, "green", "Chrome already running");
        return;
    }

    console.log('[Chrome Starter] Initiating Chrome start process');
    node.status({ fill: "blue", shape: "dot", text: "Starting Chrome..." });
    console.log('[Chrome Starter] Executing command:', chromePath, chromeArgs.join(' '));

    // Add a 300ms delay before starting Chrome
    setTimeout(() => {
        try {
            const chromeProcess = childProcess.spawn(chromePath, chromeArgs, {
                detached: true,
                stdio: 'ignore'
            });

            chromeProcess.on('error', (err) => {
                console.error('[Chrome Starter] Failed to start Chrome:', err);
                sendOutput({
                    payload: `Failed to start Chrome: ${err.message}`,
                    chromeStarted: false,
                    error: err,
                    errorMessage: err.message
                }, "red", "Chrome start error");
            });

            chromeProcess.unref();

            // Start checking immediately after spawning the process
            setTimeout(checkChromeRunning, 1000);
        } catch (err) {
            console.error('[Chrome Starter] Error when trying to start Chrome:', err);
            sendOutput({
                payload: `Error when trying to start Chrome: ${err.message}`,
                chromeStarted: false,
                error: err,
                errorMessage: err.message
            }, "red", "Chrome start error");
        }
    }, 300); // 300ms delay before starting Chrome
}

function endChromeProcess(chromePath) {
    if (!context.get('chromeRunning')) {
        console.log('[Chrome Starter] No Chrome Canary process to end');
        sendOutput({
            payload: "No Chrome Canary process to end",
            chromeEnded: false,
            error: null,
            errorMessage: null
        }, "yellow", "No Chrome Canary process");
        return;
    }

    if (!chromePath) {
        console.error('[Chrome Starter] chromePath is undefined or null');
        sendOutput({
            payload: "chromePath is undefined",
            chromeEnded: false,
            error: true,
            errorMessage: 'chromePath is undefined or null'
        }, "red", "Error");
        return;
    }

    console.log('[Chrome Starter] Gracefully ending Chrome Canary process');

    const osType = getOperatingSystem();

    if (osType === 'windows') {
        // Ensure chromePath is properly escaped for the command line
        const escapedChromePath = chromePath.replace(/\\/g, '\\\\');
        const command = `wmic process where "name='chrome.exe' and commandline like '%${escapedChromePath}%'" call terminate`;
        childProcess.exec(command, (error, stdout, stderr) => {
            if (error) {
                console.error('[Chrome Starter] Error closing Chrome Canary processes:', stderr || error);
                forceKillChrome();
                return;
            }

            console.log('[Chrome Starter] Chrome Canary processes closed, waiting briefly for processes to end');
            setTimeout(checkChromeProcesses, 2000); // Wait 2 seconds before checking
        });
    } else if (osType === 'mac' || osType === 'linux') {
        // Use pgrep to find the process ID and then send a quit signal
        childProcess.exec(`pgrep -f "${chromePath}"`, (error, stdout, stderr) => {
            if (error) {
                console.error('[Chrome Starter] Error finding Chrome Canary processes:', stderr || error);
                forceKillChrome();
                return;
            }

            const pids = stdout.trim().split('\n').filter(pid => pid.trim() !== '');
            if (pids.length > 0) {
                console.log(`[Chrome Starter] Found Chrome Canary process(es): ${pids.join(', ')}. Attempting to gracefully quit each...`);
                pids.forEach(pid => {
                    childProcess.exec(`kill -15 ${pid}`, (error, stdout, stderr) => {
                        if (error) {
                            console.error(`[Chrome Starter] Error sending quit signal to PID ${pid}:`, stderr || error);
                        } else {
                            console.log(`[Chrome Starter] Quit signal sent to PID ${pid}.`);
                        }
                    });
                });

                // Delay to allow processes to quit gracefully
                setTimeout(checkChromeProcesses, 5000); // 5 seconds before checking
            } else {
                console.log('[Chrome Starter] No Chrome Canary process found to quit');
                sendOutput({
                    payload: "No Chrome Canary process found to quit",
                    chromeEnded: true,
                    error: null,
                    errorMessage: null
                }, "yellow", "No Chrome Canary process found");
            }
        });
    } else {
        console.error('[Chrome Starter] Unsupported OS detected');
    }
}


function checkChromeProcesses() {
    const osType = getOperatingSystem();

    if (osType === 'windows') {
        childProcess.exec('tasklist /FI "IMAGENAME eq chrome.exe" /FO CSV /NH /FI "WINDOWTITLE eq Google Chrome Canary"', (error, stdout, stderr) => {
