function forceKillChrome() {
    const osType = getOperatingSystem();

    console.log(`[Chrome Starter] Detected OS: ${osType}`);

    if (osType === 'windows') {
        console.log('[Chrome Starter] Attempting to force kill Chrome Canary on Windows using taskkill...');
        childProcess.exec('taskkill /F /IM chrome.exe /FI "WINDOWTITLE eq Google Chrome Canary"', (error, stdout, stderr) => {
            if (error) {
                console.error('[Chrome Starter] Error force killing Chrome Canary process:', error);
                console.error('[Chrome Starter] Command output:', stdout);
                console.error('[Chrome Starter] Command error output:', stderr);
                sendOutput({
                    payload: "Error ending Chrome Canary process",
                    chromeEnded: false,
                    error: error,
                    errorMessage: error.message || JSON.stringify(error)
                }, "red", "Error ending Chrome Canary");
            } else {
                console.log('[Chrome Starter] Chrome Canary process force killed successfully. Command output:', stdout);
                context.set('chromeRunning', false, 'file');
                sendOutput({
                    payload: "Chrome Canary process force killed successfully",
                    chromeEnded: true,
                    error: null,
                    errorMessage: null
                }, "blue", "Chrome Canary ended (forced)");
            }
        });
    } else if (osType === 'mac' || osType === 'linux') {
        console.log(`[Chrome Starter] Attempting to find Chrome Canary processes on ${osType} using pgrep...`);
        childProcess.exec('pgrep -f "Google Chrome Canary"', (error, stdout, stderr) => {
            console.log('[Chrome Starter] pgrep command output:', stdout);
            console.log('[Chrome Starter] pgrep command error output:', stderr);
            const pids = stdout.trim().split('\n');

            if (pids.length === 0 || pids[0] === '') {
                console.log('[Chrome Starter] No Chrome Canary process found to kill');
                sendOutput({
                    payload: "No Chrome Canary process found to kill",
                    chromeEnded: true,
                    error: null,
                    errorMessage: null
                }, "yellow", "No Chrome Canary process found");
            } else {
                console.log(`[Chrome Starter] Found Chrome Canary process(es): ${pids.join(', ')}. Attempting to force kill each...`);

                let killErrors = [];
                let killSuccess = false;

                pids.forEach(pid => {
                    console.log(`[Chrome Starter] Attempting to kill process ID: ${pid}`);
                    childProcess.exec(`kill -9 ${pid}`, (error, stdout, stderr) => {
                        if (error) {
                            console.error(`[Chrome Starter] Error force killing Chrome Canary process with PID ${pid}:`, error);
                            console.error(`[Chrome Starter] kill command output for PID ${pid}:`, stdout);
                            console.error(`[Chrome Starter] kill command error output for PID ${pid}:`, stderr);
                            killErrors.push(`Error killing PID ${pid}: ${error.message}`);
                        } else {
                            console.log(`[Chrome Starter] Successfully killed Chrome Canary process with PID ${pid}.`);
                            killSuccess = true;
                        }
                    });
                });

                setTimeout(() => {
                    if (killSuccess) {
                        context.set('chromeRunning', false, 'file');
                        sendOutput({
                            payload: "Chrome Canary process(es) force killed successfully",
                            chromeEnded: true,
                            error: null,
                            errorMessage: null
                        }, "blue", "Chrome Canary ended (forced)");
                    } else {
