                    }
                }, 1000);  // Delay to allow all kill commands to complete
            }
        });
    } else {
        console.error('[Chrome Starter] Unsupported OS detected');
    }
}

function restartChromeProcess() {
    console.log('[Chrome Starter] Restart process initiated');

