function processNextPrompt() {
    console.log(`[sectionController] Processing next prompt. Current step: ${currentStep + 1}`);
    if (currentStep < promptSections.length) {
        const newMsg = {
            ...msg,  // Spread all properties from the original msg
            promptSectionContext: {
                ...msg.promptSectionContext,
                promptSectionsArray: promptSections,
                currentStep: currentStep,
                totalSteps: promptSections.length,
                promptConfig: promptSections[currentStep],
                status: "processing",
                _continuation: true,
                _event: "",
            },
            payload: promptSections[currentStep].body,
            sidePromptContext: {
                ...msg.sidePromptContext,
                sidePromptCmdDone: null
            },
            inputContext: {
                ...msg.inputContext,
                filePrompt: "",
                filePath: ""
            },
            dynamicCmdContext: {
                ...msg.dynamicCmdContext,
                dynamicCmd: false,
                dynamicCmdComplete: false,
                commandsArray: [],
                currentOutputType: "mainPrompt",
                cmdExecTime: Date.now(),
