            }
        };
        console.log(`[sectionController] Sending prompt ${currentStep + 1} of ${promptSections.length}`);
        node.send(newMsg);
        node.status({ fill: "blue", shape: "dot", text: `Processing prompt ${currentStep + 1} of ${promptSections.length}` });
    } else {
        console.log('[sectionController] All promptSections processed. Stopping.');
        stopProcessing();
    }
