function startProcessing() {
    console.log('[sectionController] Starting processing');
    isProcessing = true;
    currentStep = 0;
    context.set('isProcessing', isProcessing);
    context.set('currentStep', currentStep);
    processNextPrompt();
}

function stopProcessing() {
    console.log('[sectionController] Stopping processing');
    isProcessing = false;
    currentStep = 0;
    promptSections = [];
    context.set('isProcessing', false);
    context.set('currentStep', 0);
    context.set('promptSections', []);
    node.status({ fill: "yellow", shape: "dot", text: "Sequence Stopped, promptSections Unloaded" });
    // Send message to the Playwright function node if not last msg
    if (msg.promptSectionContext && msg.promptSectionContext._continuation == true) {
        node.send({
            ...msg,
            payload: "stop",
            promptSectionContext: {
                ...msg.promptSectionContext,
                status: "stop"
            }
        });
    }
}

if (msg.promptSectionContext && msg.promptSectionContext._continuation) {
    console.log(`[sectionController] Received continuation message with payload: ${msg.payload}`);
    if (isProcessing) {
        if (msg.promptSectionContext.status === "done" || msg.promptSectionContext._event === "End of Sequence") {
            console.log('[sectionController] Received "done" or "End of Sequence" signal. Processing next prompt.');
            currentStep++;
            context.set('currentStep', currentStep);
            processNextPrompt();
        } else if (msg.promptSectionContext.status === "error") {
            console.log(`[sectionController] Error occurred: ${msg.error}. Stopping sequence.`);
            stopProcessing();
        }
    }
} else {
    console.log(`[sectionController] Received new message with payload: ${msg.payload}`);
    if (msg.promptSectionContext && msg.promptSectionContext.status === "start") {
        // Load promptSections if they haven't been loaded yet
        if (!promptSections.length) {
            passedPromptSections();
            console.log("promptSections.length : ", promptSections.length);
        }
        startProcessing();
    } else if (msg.promptSectionContext && msg.promptSectionContext.status === "stop") {
        stopProcessing();
    } else if (msg.promptSectionContext && msg.promptSectionContext.status === "reload") {
        passedPromptSections();
    } else if (msg.promptSectionContext && msg.promptSectionContext._event === "End of Sequence") {
        stopProcessing();
        console.log('[sectionController] All promptSections processed. Stopping.');
        node.status({ fill: "green", shape: "dot", text: "All promptSections processed. Stopping." });
    } else {
        console.log(`[sectionController] Unrecognized message: ${msg.payload}`);
    }
}

