function parsePromptSection(section) {
    const [title, ...bodyLines] = section.split('\n');
    let body = bodyLines.join('\n').trim();

    console.log(`[promptLoader] Parsing prompt section: ${title}`);
    console.log(`[promptLoader] Body: ${body.substring(0, 100)}...`); // Log first 100 chars to avoid cluttering

    return {
        title: title.trim(),
        body: body
    };
}

function parsePromptsFromContent(content) {
    const sections = content.split('\n# ');
    return sections.slice(1).map(section => parsePromptSection(section));
}

function loadPrompts(msg) {
