async function waitForResponseCompletion() {
    console.log('[Run Prompt Side] Entering waitForResponseCompletion');
    console.log('[Run Prompt Side] Initializing variables');

    let previousLength = 0, currentLength = 0, stableCount = 0;
    const maxStableCount = 3;
    const maxWaitTime = 120000; // 2 minutes
    const startTime = Date.now();
    let responseText = '';

    console.log(`[Run Prompt Side] Max stable count: ${maxStableCount}, Max wait time: ${maxWaitTime}ms`);

    while (Date.now() - startTime < maxWaitTime) {
        console.log(`[Run Prompt Side] Elapsed time: ${Date.now() - startTime}ms`);

        if (context.runPrompt.isStopRequested) {
            console.log('[Run Prompt Side] Stop requested. Halting response completion.');
            await stopGeneration();
            clearStopRequestedState();
            return '';
        }

        try {
            console.log('[Run Prompt Side] Attempting to get current page');
            const page = await getCurrentPage();
            console.log('[Run Prompt Side] Successfully got current page');

            // Check for scroll-to-bottom button and click if visible
            console.log('[Run Prompt Side] Checking for scroll-to-bottom button');
            const scrollButtonSelector = 'button.cursor-pointer.absolute.z-10.rounded-full.bg-clip-padding.border.text-token-text-secondary.border-token-border-light.right-1\\/2.translate-x-1\\/2.bg-token-main-surface-primary.w-8.h-8.flex.items-center.justify-center.bottom-5';
            const scrollButtonClicked = await clickButtonIfVisible(scrollButtonSelector);

            if (scrollButtonClicked) {
                console.log('[Run Prompt Side] Scroll-to-bottom button clicked, waiting for scroll animation');
                await page.waitForTimeout(1000); // Wait for scroll animation
                console.log('[Run Prompt Side] Scroll animation wait complete');
            } else {
                console.log('[Run Prompt Side] Scroll-to-bottom button not found or not clicked');
            }

            console.log('[Run Prompt Side] Waiting for article selector');
            await page.waitForSelector('div.h-full article.w-full.text-token-text-primary', { timeout: 10000 });
            console.log('[Run Prompt Side] Article selector found');

            console.log('[Run Prompt Side] Querying for articles');
            const articles = await page.$$('div.h-full article.w-full.text-token-text-primary');
            console.log(`[Run Prompt Side] Found ${articles.length} articles`);

            if (articles.length === 0) {
                console.log('[Run Prompt Side] No articles found, waiting...');
                await page.waitForTimeout(3000);
                continue;
            }

            const lastArticle = articles[articles.length - 1];
            console.log('[Run Prompt Side] Attempting to extract content from last article');
            try {
                responseText = await page.evaluate(article => {
                    const markdownDiv = article.querySelector('.markdown.prose.w-full.break-words.dark\\:prose-invert.dark');
                    return markdownDiv ? markdownDiv.innerText : '';
                }, lastArticle);
                console.log('[Run Prompt Side] Content extracted successfully');
            } catch (error) {
                console.log('[Run Prompt Side] Error extracting content:', error.message);
                console.log('[Run Prompt Side] Falling back to article.innerText()');
                responseText = await lastArticle.innerText();
                console.log('[Run Prompt Side] Fallback content extraction complete');
            }

            currentLength = responseText.length;
            console.log(`[Run Prompt Side] Current response length: ${currentLength}`);
            if (currentLength == 0) {
                console.log('[Run Prompt Side] No content found in the response');
            }

            const isStable = currentLength === previousLength && currentLength > 0;
            stableCount = isStable ? stableCount + 1 : 0;
            console.log(`[Run Prompt Side] Is response stable: ${isStable}, Stable count: ${stableCount}`);

            if (stableCount >= maxStableCount) {
                console.log('[Run Prompt Side] Response appears to be complete');
                return responseText;
            }

            previousLength = currentLength;
            console.log('[Run Prompt Side] Waiting for 2 seconds before next iteration');
            await page.waitForTimeout(2000);
        } catch (error) {
            console.log(`[Run Prompt Side] Error in waitForResponseCompletion: ${error.message}`);
            console.log('[Run Prompt Side] Error stack:', error.stack);
            console.log('[Run Prompt Side] Waiting for 3 seconds before retrying');
            await new Promise(resolve => setTimeout(resolve, 3000));
        }
    }

    console.log('[Run Prompt Side] Max wait time exceeded. Returning current response.');
    return responseText;
}

