}

async function clickButtonIfVisible(selector, waitForHidden = false) {
    try {
        const page = await getCurrentPage();
        const button = await page.$(selector);
        const shouldClick = button && await button.isVisible();

        if (shouldClick) {
            await button.click();

            if (waitForHidden) {
                await page.waitForSelector(selector, { state: 'hidden', timeout: 60000 });
            }
        }

        return shouldClick;
    } catch (error) {
        return false;
    }
}

async function reloadPageIfNecessary(retryCount) {
    console.log('[Run Prompt] Entering reloadPageIfNecessary:', { retryCount });
    try {
        const page = await getCurrentPage();
        const shouldReload = [
            retryCount > 0,
            !await clickButtonIfVisible('button:has-text("Continue generating")', true),
            await page.$('#prompt-textarea').then(() => false).catch(() => true)
        ].every(Boolean);

        console.log('[Run Prompt] Should reload:', shouldReload);
        shouldReload && await page.reload({ waitUntil: 'networkidle' });
        console.log('[Run Prompt] Page reloaded:', shouldReload);
    } catch (error) {
        console.log(`[Run Prompt] Error in reloadPageIfNecessary: ${error.message}`);
    }
}

async function waitForResponseCompletion() {
    console.log('[Run Prompt] Entering waitForResponseCompletion');
    console.log('[Run Prompt] Initializing variables');

    let previousLength = 0, currentLength = 0, stableCount = 0;
    const maxStableCount = 3;
    const maxWaitTime = 120000; // 2 minutes
    const startTime = Date.now();
    let responseText = '';

    console.log(`[Run Prompt] Max stable count: ${maxStableCount}, Max wait time: ${maxWaitTime}ms`);

    while (Date.now() - startTime < maxWaitTime) {
        console.log(`[Run Prompt] Elapsed time: ${Date.now() - startTime}ms`);

        if (context.runPrompt.isStopRequested) {
            console.log('[Run Prompt] Stop requested. Halting response completion.');
            await stopGeneration();
            clearStopRequestedState();
            return '';
        }

        try {
            console.log('[Run Prompt] Attempting to get current page');
            const page = await getCurrentPage();
            console.log('[Run Prompt] Successfully got current page');

            // Check for scroll-to-bottom button and click if visible
            console.log('[Run Prompt] Checking for scroll-to-bottom button');
            const scrollButtonSelector = 'button.cursor-pointer.absolute.z-10.rounded-full.bg-clip-padding.border.text-token-text-secondary.border-token-border-light.right-1\\/2.translate-x-1\\/2.bg-token-main-surface-primary.w-8.h-8.flex.items-center.justify-center.bottom-5';
            const scrollButtonClicked = await clickButtonIfVisible(scrollButtonSelector);

            if (scrollButtonClicked) {
                console.log('[Run Prompt] Scroll-to-bottom button clicked, waiting for scroll animation');
                await page.waitForTimeout(1000); // Wait for scroll animation
                console.log('[Run Prompt] Scroll animation wait complete');
            } else {
                console.log('[Run Prompt] Scroll-to-bottom button not found or not clicked');
            }

            console.log('[Run Prompt] Waiting for article selector');
            await page.waitForSelector('div.h-full article.w-full.text-token-text-primary', { timeout: 10000 });
            console.log('[Run Prompt] Article selector found');

            console.log('[Run Prompt] Querying for articles');
            const articles = await page.$$('div.h-full article.w-full.text-token-text-primary');
            console.log(`[Run Prompt] Found ${articles.length} articles`);

            if (articles.length === 0) {
                console.log('[Run Prompt] No articles found, waiting...');
                await page.waitForTimeout(3000);
                continue;
            }

            const lastArticle = articles[articles.length - 1];
            console.log('[Run Prompt] Attempting to extract content from last article');
            try {
                responseText = await page.evaluate(article => {
                    const markdownDiv = article.querySelector('.markdown.prose.w-full.break-words.dark\\:prose-invert.dark');
                    return markdownDiv ? markdownDiv.innerText : '';
                }, lastArticle);
                console.log('[Run Prompt] Content extracted successfully');
            } catch (error) {
                console.log('[Run Prompt] Error extracting content:', error.message);
                console.log('[Run Prompt] Falling back to article.innerText()');
                responseText = await lastArticle.innerText();
                console.log('[Run Prompt] Fallback content extraction complete');
            }

            currentLength = responseText.length;
            console.log(`[Run Prompt] Current response length: ${currentLength}`);
            if (currentLength == 0) {
                console.log('[Run Prompt] No content found in the response');
            }

            const isStable = currentLength === previousLength && currentLength > 0;
            stableCount = isStable ? stableCount + 1 : 0;
            console.log(`[Run Prompt] Is response stable: ${isStable}, Stable count: ${stableCount}`);

            if (stableCount >= maxStableCount) {
                console.log('[Run Prompt] Response appears to be complete');
                return responseText;
            }

            previousLength = currentLength;
            console.log('[Run Prompt] Waiting for 2 seconds before next iteration');
            await page.waitForTimeout(2000);
        } catch (error) {
            console.log(`[Run Prompt] Error in waitForResponseCompletion: ${error.message}`);
            console.log('[Run Prompt] Error stack:', error.stack);
            console.log('[Run Prompt] Waiting for 3 seconds before retrying');
            await new Promise(resolve => setTimeout(resolve, 3000));
        }
    }

    console.log('[Run Prompt] Max wait time exceeded. Returning current response.');
    return responseText;
}

async function extractResponseText(currentPage) {
    console.log('[Run Prompt] Entering extractResponseText');
    let responseText = '';
    let lastArticle;

    try {
        // Find all articles within the h-full div
        const articles = await currentPage.$$('div.h-full article.w-full.text-token-text-primary');
        console.log(`[Run Prompt] Found ${articles.length} articles`);

        if (articles.length > 0) {
            // Get the last article
            lastArticle = articles[articles.length - 1];
            console.log('[Run Prompt] Selected the last article');
        } else {
            throw new Error('No articles found');
        }

        // Find and click the copy button
        const copyButton = await lastArticle.$('button[aria-label="Copy"]');
        if (copyButton) {
            await copyButton.click();
            await currentPage.waitForTimeout(1000); // Wait for clipboard to be updated

            // Get clipboard contents
            responseText = await currentPage.evaluate(() => navigator.clipboard.readText());
            console.log('[Run Prompt] Response extracted from clipboard');
        } else {
            console.log('[Run Prompt] Copy button not found, falling back to original method');
            const markdownDiv = await lastArticle.$('div.markdown');
            if (markdownDiv) {
                responseText = await markdownDiv.innerText();
            } else {
                throw new Error('Markdown div not found');
            }
        }
    } catch (error) {
        console.log('[Run Prompt] Error extracting content:', error.message);
        try {
            if (!lastArticle) {
                lastArticle = await currentPage.$('div.h-full article.w-full.text-token-text-primary:last-child');
                if (!lastArticle) {
                    throw new Error('Unable to find last article');
                }
            }
            const textBaseElement = await lastArticle.$('.text-base');
            if (textBaseElement) {
                responseText = await textBaseElement.innerText();
                console.log('[Run Prompt] Response extracted using alternative selector');
            } else {
                throw new Error('Alternative selector not found');
            }
        } catch (innerError) {
            console.log('[Run Prompt] Alternative selector also failed, using entire article content');
            if (lastArticle) {
                responseText = await lastArticle.innerText();
            } else {
                console.log('[Run Prompt] Failed to extract any content');
                responseText = '';
            }
        }
    }

    console.log('[Run Prompt] Exiting extractResponseText, response length:', responseText.length);
    return responseText;
}

async function stopGeneration() {
    console.log('[Run Prompt] Entering stopGeneration');
    context.runPrompt.isStopRequested = true;
    await clickButtonIfVisible('button[data-testid="stop-button"][aria-label="Stop streaming"]', true);
    clearStopRequestedState();
    console.log('[Run Prompt] Exiting stopGeneration');
}

function clearStopRequestedState() {
    context.runPrompt.isStopRequested = false;
    console.log('[Run Prompt] Stop requested state cleared.');
}

function cleanup(browser, result, msg) {
    if (browser) {
        browser.close().then(() => console.log('[Run Prompt] Browser closed'));
    }

    node.status(result.status);

    const isLastMessage = result.endOfSequence;

    const firstOutput = {
        ...msg,
        payload: result.payload,
        endOfSequence: !!isLastMessage,
        _continuation: !isLastMessage  // Set _continuation to true for the last message
    };

    const secondOutput = {
        payload: result.status.text,
        _continuation: !isLastMessage,
        error: result.error,
        endOfSequence: isLastMessage === true
    };

    node.send([firstOutput, secondOutput]);

    return result;
}

async function runPrompt(msg) {
    console.log('[Run Prompt] Entering runPrompt');
    try {
        await reloadPageIfNecessary(0);

        const page = await getCurrentPage();
        console.log('[Run Prompt] Got current page in runPrompt');

        const newChatButton = await page.$('nav > a.flex.py-3.px-3.items-center.gap-3.rounded-md:has-text("New chat")');
