            console.log('[Run Prompt] Found new chat button, clicking...');
            await newChatButton.click();
            await page.waitForLoadState('networkidle');
            console.log('[Run Prompt] Clicked new chat button');
        } else {
            console.log('[Run Prompt] New chat button not found');
        }

        await clickButtonIfVisible('button:has-text("Continue generating")', true);

        const promptText = msg.payload || 'Give me a recipe for biscuits';
        console.log('[Run Prompt] Entering prompt:', promptText);
        await page.waitForSelector('#prompt-textarea', { state: 'attached', timeout: 30000 });
        await page.fill('#prompt-textarea', promptText);
        await page.click('button[data-testid="send-button"]');
