function parsePromptSection(section) {
    const [title, ...bodyLines] = section.split('\n');
    let body = bodyLines.join('\n').trim();

    console.log(`[promptLoader] Parsing prompt section: ${title}`);
    console.log(`[promptLoader] Body: ${body.substring(0, 100)}...`); // Log first 100 chars to avoid cluttering

    return {
        title: title.trim(),
        body: body
    };
}

function parsePromptsFromContent(content) {
    const sections = content.split('\n# ');
    return sections.slice(1).map(section => parsePromptSection(section));
}

function loadPrompts(msg) {
    if (shouldSkipLoading(msg)) {
        return null;
    }

    try {
        const filePath = getFilePath(msg);
        const content = fs.readFileSync(filePath, 'utf8');
        let prompts = parsePromptsFromContent(content);

        console.log(`[promptLoader] Loaded ${prompts.length} prompts`);
        node.status({ fill: "blue", shape: "dot", text: `Loaded ${prompts.length} prompts` });

        return prompts;
    } catch (error) {
        console.error(`[promptLoader] Failed to load prompts: ${error.message}`);
        node.error(`Failed to load prompts: ${error.message}`);
        node.status({ fill: "red", shape: "ring", text: "Failed to load prompts" });
        return null;
    }
}

// Usage
try {
    const promptsArray = loadPrompts(msg);
    console.log('[promptLoader] promptsArray length:', promptsArray ? promptsArray.length : 0);

    if (promptsArray) {
        // Create a new object with all properties of the original message
        const newMsg = { ...msg };
        // Add the promptsArray to the new message object
        newMsg.promptsArray = promptsArray;
        return newMsg;
    } else {
        // If prompts weren't loaded successfully, return the original message
        return msg;
    }
} catch (error) {
    console.error(`[promptLoader] Unexpected error: ${error.message}`);
    node.error(`Unexpected error: ${error.message}`);
    return null;
}

// Consolidated function to parse dynamic variables
function parseDynamicVariables(text, msg, context, dynamicCmd) {
    console.log('[parseDynamicVariables] Starting function');

    if (!msg || typeof msg !== 'object') {
        console.warn('[parseDynamicVariables] Warning: msg is not a valid object');
        return text;
    }

    const objectReplacements = new Map();
    let counter = 0;

    function resolveValue(path) {
        console.log(`[parseDynamicVariables] Resolving value for path: ${path}`);

        // Handle execute() function
        const executeMatch = path.match(/^execute\((.*)\)$/);
        if (executeMatch) {
            let command = executeMatch[1].trim();
            try {
                if (command.endsWith(';')) {
                    command = command.slice(0, -1);
                }
                const dynamicFunction = new Function('context', 'msg', `return ${command}`);
                const value = dynamicFunction(context, msg);
                console.log(`[parseDynamicVariables] Executed command: ${command}, Result: ${value}`);
                return value !== undefined ? value : `[${command} returned undefined]`;
            } catch (error) {
                console.error(`[parseDynamicVariables] Error executing command: ${error.message}`);
                return `[Error executing command: ${error.message}]`;
            }
        }

        // Handle {previousResponse}
        if (path === '{previousResponse}') {
            if (msg && typeof msg.currentStep !== 'undefined') {
                const currentStep = msg.currentStep || 0;
                const previousResponseNumber = currentStep - 1;
                if (previousResponseNumber >= 0 && msg[`response${previousResponseNumber}`]) {
                    return msg[`response${previousResponseNumber}`];
                }
            }
            return '[Previous response not available]';
        }

        // Handle $<<commandTypeNumberResult>>$
        const cmdResultMatch = path.match(/^(\w+)(\d+)Result$/);
        if (cmdResultMatch && dynamicCmd) {
            const [, cmdType, cmdNumber] = cmdResultMatch;
            const targetCmd = dynamicCmd.sections.flatMap(section => section.commands)
                .find(c => c.type === cmdType && c.number === cmdNumber);
            if (targetCmd && targetCmd.result !== undefined) {
                return targetCmd.result;
            }
            return `[No result found for ${cmdType}${cmdNumber}]`;
        }

        // Regular path traversal
        const parts = path.split('.');
        let value = msg;
        const startIndex = parts[0] === 'msg' ? 1 : 0;
        for (let i = startIndex; i < parts.length; i++) {
            if (value && typeof value === 'object' && parts[i] in value) {
                value = value[parts[i]];
            } else {
                console.warn(`[parseDynamicVariables] Warning: ${parts[i]} does not exist in the object`);
                return `[${path} not available]`;
            }
        }
        return value;
    }

    function handleUndefinedOrNull(path, value) {
        if (value === undefined || value === null) {
            console.warn(`[parseDynamicVariables] Warning: ${path} is ${value === undefined ? 'undefined' : 'null'}`);
            return `[${path} not available]`;
        }
        return null;
    }

    function handleObject(path, value) {
        if (typeof value === 'object') {
            const marker = `__OBJECT_REPLACEMENT_${counter}__`;
            objectReplacements.set(marker, value);
            counter++;
            return marker;
        }
        return null;
    }

    // Replace dynamic variables
    const intermediateResult = text.replace(/\$<<([^>]+)>>\$|\{([^}]+)\}/g, (match, p1, p2) => {
        const path = p1 || p2;
        const value = resolveValue(path);

        const undefinedOrNullResult = handleUndefinedOrNull(path, value);
        if (undefinedOrNullResult) return undefinedOrNullResult;

        const objectResult = handleObject(path, value);
        if (objectResult) return objectResult;

        return String(value);
    });

    // Replace object markers
    let finalResult = intermediateResult;
    for (const [marker, value] of objectReplacements) {
        finalResult = finalResult.replace(marker, JSON.stringify(value));
    }

    console.log(`[parseDynamicVariables] Final result: ${finalResult}`);
    return finalResult;
}

// 2. Generalized function to execute commands with added safety checks
function executeCommands(commands, context, msg) {
    let resultAccumulator = "";

    commands.forEach((cmdBlock, index) => {
        console.log(`[function:dynamicCmd] Executing command block: ${cmdBlock.name || `Command ${index + 1}`}`);
        console.log(`[function:dynamicCmd] Command content: ${JSON.stringify(cmdBlock)}`);

        if (cmdBlock.commands.length === 0) {
            console.log(`[function:dynamicCmd] No commands found in: ${cmdBlock.name || `Command ${index + 1}`}`);
        }

        cmdBlock.commands.forEach(cmd => {
            if (cmd.trim() === '') {
                console.log(`[function:dynamicCmd] Skipping empty command.`);
                return; // Skip empty commands
            }

            console.log(`[function:dynamicCmd] Executing command: ${cmd}`);

            try {
                // Dynamically create and execute the function with a timeout to prevent hanging
                let result;
                const dynamicFunction = new Function('context', 'msg', cmd);
                const timeout = setTimeout(() => {
                    throw new Error("Command execution timed out.");
                }, 5000); // Set a timeout of 5 seconds

                result = dynamicFunction(context, msg);  // Pass context and execute the function
                clearTimeout(timeout); // Clear the timeout if execution completes

                // Check if the function returned a value
                if (result !== undefined) {
                    console.log(`[function:dynamicCmd] Command executed successfully with result: ${result}`);
                    resultAccumulator += result + "\n";  // Accumulate results
                } else {
                    console.log(`[function:dynamicCmd] Command executed successfully but returned no result.`);
                }
            } catch (error) {
                console.error(`[function:dynamicCmd] Error executing command: ${error.message}`);
                resultAccumulator += `[Error executing command: ${error.message}]\n`; // Accumulate error message
            }
        });
    });

    console.log(`[function:dynamicCmd] Finished executing all commands. Final accumulated result: ${resultAccumulator.trim()}`);
    return resultAccumulator.trim();
}

// Part 2: Command Processing and Side Prompt Handling

// 3. Function to process compileCmd or packCmd
function processCompileOrPackCmd(cmdArray, localResult, context, dynamicCmd, msg, cmdType) {
    let compiledPrompt = "";
    localResult = localResult || "";

    console.log(`[processCompileOrPackCmd] Initial localResult: ${localResult}`);
    console.log(`[processCompileOrPackCmd] Initial ${cmdType} array: ${JSON.stringify(cmdArray)}`);

    cmdArray.forEach(cmd => {
        console.log(`[processCompileOrPackCmd] Processing command: ${cmd}`);
        cmd = parseDynamicVariables(cmd, msg, context, dynamicCmd);
        compiledPrompt += cmd + "\n";
    });

    console.log(`[processCompileOrPackCmd] Final ${cmdType} prompt:\n${compiledPrompt}`);
    return compiledPrompt;
}

// Function to process sidePrompt blocks
async function processSidePrompt(sidePromptCmds, context, msg) {
    console.log(`[processSidePrompt] Starting processSidePrompt function.`);
    console.log(`[processSidePrompt] Number of commands to process: ${sidePromptCmds.length}`);

    // 1. Compile commands into loadedSidePrompt array
    const loadedSidePrompt = sidePromptCmds.map(cmd => parseDynamicVariables(cmd, msg, context));

    console.log(`[processSidePrompt] Compiled loadedSidePrompt: ${JSON.stringify(loadedSidePrompt)}`);

    // 2. Send loadedSidePrompt out of output2
    node.send([null, { payload: loadedSidePrompt, status: "start", sidePromptCmd: true }]);
    console.log(`[processSidePrompt] Sent loadedSidePrompt to output2.`);

    // 3. Wait for sidePrompt completion
    console.log(`[processSidePrompt] Waiting for sidePrompt completion...`);
    const result = await waitForSidePromptCompletion();

    console.log(`[processSidePrompt] SidePrompt completion received.`);

    console.log(`[processSidePrompt] SidePrompt processing complete.`);
    return { loadedSidePrompt, result };
}

// Function to wait for sidePrompt completion
function waitForSidePromptCompletion() {
    return new Promise((resolve) => {
        // Set the global waiting state
        global.waitingForSidePrompt = true;
        console.log(`[waitForSidePromptCompletion] Set waitingForSidePrompt to true`);

        // Function to check completion status
        const checkCompletion = () => {
            console.log(`[waitForSidePromptCompletion] Checking completion status...`);

            if (!global.waitingForSidePrompt) {
                console.log(`[waitForSidePromptCompletion] Side prompt completed`);
                resolve(global.sidePromptResult);
            } else {
                console.log(`[waitForSidePromptCompletion] Still waiting, will check again in 1300ms`);
                setTimeout(checkCompletion, 1300);
            }
        };

        // Start checking
        checkCompletion();
    });
}

// Helper function to set side prompt completion
function setSidePromptCompletion(result) {
    global.waitingForSidePrompt = false;
    global.sidePromptResult = result;
    console.log(`[setSidePromptCompletion] Set waitingForSidePrompt to false and stored result`);
}

// Function to process mainPromptCmd
function processMainPromptCmd(mainPromptCmd, dynamicCmd, msg) {
    console.log(`[processMainPromptCmd] Processing mainPromptCmd: ${JSON.stringify(mainPromptCmd)}`);
    console.log(`[processMainPromptCmd] dynamicCmd structure: ${JSON.stringify(dynamicCmd, null, 2)}`);

    let processedLines = [];
    let indentLevel = 0;

    for (let line of mainPromptCmd) {
        console.log(`[processMainPromptCmd] Processing line: ${line}`);

        // Remove comments and trim whitespace
        line = line.replace(/\/\/.*$/, '').trim();

        if (line === '') {
            console.log(`[processMainPromptCmd] Skipping empty line`);
            continue;
        }

        // Check for indent level
        if (line.startsWith('-')) {
            indentLevel = line.match(/^\s*/)[0].length;
            line = line.substring(indentLevel).trim();
        }

        // Parse dynamic variables
        line = parseDynamicVariables(line, msg, context, dynamicCmd);

        // Add appropriate indentation
        line = ' '.repeat(indentLevel) + line;

        console.log(`[processMainPromptCmd] Processed line: ${line}`);
        processedLines.push(line);
    }

    // Join the processed lines into a single string with newline characters
    const result = processedLines.join('\n');

    console.log(`[processMainPromptCmd] Processed mainPromptCmd result:\n${result}`);
    return result;
}

// 5. Function to execute the parsed commands block by block
async function executeCommandsBlockByBlock(dynamicCmd, context, msg, node) {
    let finalResult = "";
    console.log(`[executeCommandsBlockByBlock] Starting execution of commands`);
    console.log(`[executeCommandsBlockByBlock] Number of sections: ${dynamicCmd.sections.length}`);

    for (let sectionIndex = 0; sectionIndex < dynamicCmd.sections.length; sectionIndex++) {
        const section = dynamicCmd.sections[sectionIndex];
        console.log(`[executeCommandsBlockByBlock] Processing section ${sectionIndex + 1}`);
        console.log(`[executeCommandsBlockByBlock] Number of commands in section: ${section.commands.length}`);

        for (let commandIndex = 0; commandIndex < section.commands.length; commandIndex++) {
            const command = section.commands[commandIndex];

            console.log(`[executeCommandsBlockByBlock] Executing command block: ${command.type}${command.number}`);
            console.log(`[executeCommandsBlockByBlock] Command content: ${JSON.stringify(command, null, 2)}`);

            let result;

            switch (command.type) {
                case 'localCmd':
                    try {
                        const commandString = command.commands.join('\n');
                        console.log(`[executeCommandsBlockByBlock] Executing localCmd: ${commandString}`);
                        const wrappedCommand = `(function() { ${commandString} })()`;
                        result = eval(wrappedCommand);
                        console.log(`[executeCommandsBlockByBlock] localCmd result: ${result}`);
                    } catch (error) {
                        console.error(`[executeCommandsBlockByBlock] Error executing localCmd: ${error.message}`);
                        result = `[Error executing command: ${error.message}]`;
                    }
                    break;
                case 'compileCmd':
                case 'packCmd':
                    console.log(`[executeCommandsBlockByBlock] Executing ${command.type}`);
                    result = processCompileOrPackCmd(command.commands, finalResult, context, dynamicCmd, msg, command.type);
                    console.log(`[executeCommandsBlockByBlock] ${command.type} result: ${JSON.stringify(result, null, 2)}`);
                    break;
                case 'sidePromptCmd':
                    console.log(`[executeCommandsBlockByBlock] Executing sidePromptCmd`);
                    const sidePromptResult = await processSidePrompt(command.commands, context, msg);
                    command.loadedSidePrompt = sidePromptResult.loadedSidePrompt;
                    command.result = sidePromptResult.result;
                    result = command.result;
                    console.log(`[executeCommandsBlockByBlock] SidePrompt execution complete`);
                    console.log(`[executeCommandsBlockByBlock] SidePrompt result:`, JSON.stringify(command, null, 2));
                    break;
                case 'mainPromptCmd':
                    console.log(`[executeCommandsBlockByBlock] Executing mainPromptCmd`);
                    result = processMainPromptCmd(command.commands, dynamicCmd, msg);
                    console.log(`[executeCommandsBlockByBlock] mainPromptCmd result:\n${result}`);
                    break;
                default:
                    console.log(`[executeCommandsBlockByBlock] Unknown command type: ${command.type}`);
                    result = `[Unknown command type: ${command.type}]`;
            }

            command.result = result;
            if (command.type === 'mainPromptCmd') {
                finalResult = result; // For mainPromptCmd, set finalResult to the string result
            } else {
                finalResult += (finalResult ? "\n" : "") + result; // For other commands, append to finalResult
            }

            console.log(`[executeCommandsBlockByBlock] Command execution complete`);
            console.log(`[executeCommandsBlockByBlock] Updated state of ${command.type}${command.number}:`, JSON.stringify(command, null, 2));

            // Update msg.dynamicCmd after each command execution
            msg.dynamicCmd = JSON.parse(JSON.stringify(dynamicCmd));
            console.log(`[executeCommandsBlockByBlock] Updated msg.dynamicCmd`);
        }
    }

    console.log(`[executeCommandsBlockByBlock] All commands executed`);
    console.log(`[executeCommandsBlockByBlock] Final state of dynamicCmd:`, JSON.stringify(dynamicCmd, null, 2));
    console.log(`[executeCommandsBlockByBlock] Final result:\n${finalResult}`);

    return { result: finalResult, dynamicCmd: dynamicCmd };
}

// 6. Function to parse the input text and extract commands, while stripping out comments and handling indentation
// Function to parse the input text and extract commands
function parseInputText(inputText) {
    console.log(`[parseInputText] Input text: ${inputText}`);
    const lines = inputText.split('\n');
    console.log(`[parseInputText] Number of lines: ${lines.length}`);

    let dynamicCmd = { sections: [] };
    let currentCommand = null;
    let isCapturingNestedCommand = false;
    let nestedCommandBuffer = '';

    const processLine = (line) => {
        console.log(`[parseInputText] Processing line: '${line}'`);
        line = line.replace(/\/\/.*$/, '').trim(); // Remove comments
        console.log(`[parseInputText] Removed comment. New line: '${line}'`);

        if (line === 'dynamicCmd') {
            dynamicCmd.sections.push({ type: 'commandSet', commands: [] });
            console.log(`[parseInputText] Detected 'dynamicCmd' section. Created new command set.`);
            return;
        }

        if (line === '- commandSet') {
            console.log(`[parseInputText] Detected commandSet line, ignoring.`);
            return;
        }

        if (line.match(/^-\s*(\w+)(\d+)/)) {
            const [, cmdType, cmdNumber] = line.match(/^-\s*(\w+)(\d+)/);
            currentCommand = { type: cmdType, number: cmdNumber, commands: [] };
            dynamicCmd.sections[dynamicCmd.sections.length - 1].commands.push(currentCommand);
            console.log(`[parseInputText] Started new main command: ${cmdType} with number ${cmdNumber}`);
        } else if (line.startsWith('- `')) {
            isCapturingNestedCommand = true;
            nestedCommandBuffer = line.substring(3);
            console.log(`[parseInputText] Started capturing nested command`);
        } else if (isCapturingNestedCommand) {
            if (line.endsWith('`')) {
                nestedCommandBuffer += line.substring(0, line.length - 1);
                currentCommand.commands.push(nestedCommandBuffer);
                isCapturingNestedCommand = false;
                console.log(`[parseInputText] Finished capturing nested command: ${nestedCommandBuffer}`);
            } else {
                nestedCommandBuffer += line + '\n';
                console.log(`[parseInputText] Capturing nested command. Current buffer: ${nestedCommandBuffer}`);
            }
        } else if (line.startsWith('-') && currentCommand) {
            const subCommand = line.substring(1).trim();
            currentCommand.commands.push(subCommand);
            console.log(`[parseInputText] Added sub-command: ${subCommand}`);
        }
    };

    lines.forEach(processLine);

    console.log(`[parseInputText] Finished parsing input text.`);
    console.log(`[parseInputText] Final parsed dynamicCmd: ${JSON.stringify(dynamicCmd, null, 2)}`);

    return dynamicCmd;
}

// 7. Main function to handle the entire process block by block
async function handleDynamicCmd(inputText, msg, node) {
    console.log("[handleDynamicCmd] Starting handleDynamicCmd function.");
    console.log(`[handleDynamicCmd] Input text: ${inputText}`);

    if (!/dynamicCmd/.test(inputText)) {
        console.log("[handleDynamicCmd] No dynamicCmd section found. Parsing dynamic variables only.");
        msg.payload = parseDynamicVariables(inputText, msg, context);
        msg.outputType = 1; // Send to output1 by default
        console.log(`[handleDynamicCmd] Returning msg: ${JSON.stringify(msg)}`);
        return msg;
    }

    try {
        console.log("[handleDynamicCmd] Parsing input text.");
        let dynamicCmd = parseInputText(inputText);
        console.log(`[handleDynamicCmd] Parsed dynamicCmd: ${JSON.stringify(dynamicCmd)}`);
        let finalResult;
        let outputType = 0; // Default to no output

        if (!dynamicCmd || !dynamicCmd.sections || dynamicCmd.sections.length === 0) {
            console.log("[handleDynamicCmd] No valid dynamicCmd sections found after parsing. Proceeding with dynamicVars parsing.");
            finalResult = parseDynamicVariables(inputText, msg, context);
            outputType = 1; // Assume output1 for simple dynamic variable parsing
            console.log(`[handleDynamicCmd] Dynamic variables parsed. Result: ${finalResult}`);
        } else {
            console.log("[handleDynamicCmd] dynamicCmd section found. Executing commands block by block.");
            const resultData = await executeCommandsBlockByBlock(dynamicCmd, context, msg, node);
            finalResult = resultData.result;
            msg.dynamicCmd = resultData.dynamicCmd;
            console.log(`[handleDynamicCmd] Commands executed. Result: ${JSON.stringify(finalResult)}`);

            // Determine output type based on the last command executed
            const lastSection = dynamicCmd.sections[dynamicCmd.sections.length - 1];
            const lastCommand = lastSection.commands[lastSection.commands.length - 1];
            if (lastCommand.type === 'mainPromptCmd') {
                outputType = 1;
            } else if (lastCommand.type === 'sidePromptCmd') {
                outputType = 2;
            }
            console.log(`[handleDynamicCmd] Determined output type: ${outputType}`);
        }

        msg.payload = finalResult || inputText;
        msg.outputType = outputType;
        console.log(`[handleDynamicCmd] Final msg payload: ${msg.payload}`);
        console.log(`[handleDynamicCmd] Final msg outputType: ${msg.outputType}`);
    } catch (error) {
        console.error(`[handleDynamicCmd] Error occurred: ${error.message}`);
        console.error(`[handleDynamicCmd] Error stack: ${error.stack}`);
        msg.payload = `Error occurred: ${error.message}`;
        msg.outputType = 1; // Send errors to output1
    }

    console.log("[handleDynamicCmd] Finished processing. Returning message.");
    console.log(`[handleDynamicCmd] Returning msg: ${JSON.stringify(msg)}`);
    return msg;
}

// 8. Main function to process the incoming message
async function processMessage(msg, node) {
    // Update the latest message
    global.latestMsg = msg;
    console.log("[processMessage] Starting processMessage function.");
    console.log(`[processMessage] Input msg: ${JSON.stringify(msg)}`);

    // Check if this is a sidePrompt completion message
    if (msg.sidePromptStatus === "done") {
        console.log("[processMessage] Received sidePrompt completion message.");
        setSidePromptCompletion(msg.payload);
        return [null, null]; // Don't send any message for sidePrompt completion
    }

    console.log("[processMessage] Processing message with handleDynamicCmd.");
    let processedMsg = await handleDynamicCmd(msg.payload, msg, node);
    console.log(`[processMessage] Processed msg: ${JSON.stringify(processedMsg)}`);

    // Send the processed result to the appropriate output
    if (processedMsg.outputType === 1) {
        console.log("[processMessage] Sending to output1.");
        return [processedMsg, null]; // Send to output1
    } else if (processedMsg.outputType === 2) {
        console.log("[processMessage] Sending to output2.");
        return [null, processedMsg]; // Send to output2
    } else {
        console.log("[processMessage] No specific output type. Not sending any message.");
        return [null, null]; // Don't send any message
    }
}

// 9. Main execution
async function main(msg) {
    try {
        const [output1, output2] = await processMessage(msg, this);
        if (output1) node.send([output1, null]);
        if (output2) node.send([null, output2]);
    } catch (error) {
        node.error("Error processing message: " + error.message, msg);
    }
}

// 10. Call the main function
return main(msg);

// Use node context to persist state across messages
let isProcessing = context.get('isProcessing') || false;
let prompts = context.get('prompts') || [];
let currentStep = context.get('currentStep') || 0;

function processPrompts(inputPrompts) {
    console.log('[Sequence Controller Side] Processing input prompts');

    if (!Array.isArray(inputPrompts)) {
        console.error('[Sequence Controller Side] Error: Input is not an array');
        return [];
    }

    const processedPrompts = inputPrompts.map((prompt, index) => ({
        title: `Prompt ${index + 1}`,
        body: prompt,
        sidePrompt: []
    }));

    context.set('prompts', processedPrompts);
    console.log(`[Sequence Controller Side] Processed ${processedPrompts.length} prompts`);
    node.status({ fill: "blue", shape: "dot", text: `Loaded ${processedPrompts.length} prompts` });

    return processedPrompts;
}

function processNextPrompt() {
    console.log(`[Sequence Controller Side] Processing next prompt. Current step: ${currentStep + 1}`);
    if (currentStep < prompts.length) {
        const msg = {
            currentStep: currentStep,
            totalSteps: prompts.length,
            promptConfig: prompts[currentStep],
            payload: prompts[currentStep].body,
            sidePrompt: prompts[currentStep].sidePrompt,
            _continuation: true
        };
        console.log(`[Sequence Controller Side] Sending prompt ${currentStep + 1} of ${prompts.length}`);
        node.send(msg);
        node.status({ fill: "blue", shape: "dot", text: `Processing prompt ${currentStep + 1} of ${prompts.length}` });
        currentStep++;
        context.set('currentStep', currentStep);
    } else {
        console.log('[Sequence Controller Side] All prompts processed. Stopping.');
        node.send([null, { payload: "Sequence Complete", sidePromptStatus: "done" }]);
        stopProcessing();
    }
}

function startProcessing() {
    console.log('[Sequence Controller Side] Starting processing');
    isProcessing = true;
    currentStep = 0;
    context.set('isProcessing', isProcessing);
    context.set('currentStep', currentStep);
    processNextPrompt();
}

function stopProcessing() {
    console.log('[Sequence Controller Side] Stopping processing');
    isProcessing = false;
    currentStep = 0;
    prompts = [];
    context.set('isProcessing', false);
    context.set('currentStep', 0);
    context.set('prompts', []);
    node.status({ fill: "yellow", shape: "dot", text: "Sequence Stopped, Prompts Unloaded" });
}

// Main logic
if (msg._continuation) {
    console.log(`[Sequence Controller Side] Received continuation message with payload: ${msg.payload}`);
    if (isProcessing) {
        if (msg.status === "done" || msg.status === "End of Sequence") {
            console.log('[Sequence Controller Side] Received "done" or "End of Sequence" signal. Processing next prompt.');
            processNextPrompt();
        } else if (msg.status === "error") {
            console.log(`[Sequence Controller Side] Error occurred: ${msg.error}. Stopping sequence.`);
            stopProcessing();
        }
    }
} else {
    console.log(`[Sequence Controller Side] Received new message with payload: ${JSON.stringify(msg.payload)}`);
    if (msg.status === "start") {
        if (Array.isArray(msg.payload)) {
            prompts = processPrompts(msg.payload);
            startProcessing();
        } else {
            console.log('[Sequence Controller Side] Invalid input: payload is not an array of prompts');
            node.status({ fill: "red", shape: "ring", text: "Invalid input" });
        }
    } else if (msg.status === "stop") {
        stopProcessing();
    } else if (msg.status === "Sequence Complete") {
        node.send([null, { ...msg, sidePromptStatus: "done" }]);
