/**
 * Processes a single line of the input text.
 * @param {string} line - The line to process
 * @param {Object} dynamicCmd - The current state of the dynamicCmd object
 * @param {Object} currentCommand - The current command being processed
 * @param {boolean} isCapturingNestedCommand - Whether we're currently capturing a nested command
 * @param {string} nestedCommandBuffer - Buffer for storing nested command
 * @returns {Object} Updated state object
 */
function processLine(line, dynamicCmd, currentCommand, isCapturingNestedCommand, nestedCommandBuffer) {
    // Remove comments and trim whitespace
    line = line.replace(/\/\/.*$/, '').trim();

    // Control flow based on line content
    if (line === 'dynamicCmd') {
        // Start a new command set
        dynamicCmd.sections.push({ type: 'commandSet', commands: [] });
    } else if (line === '- commandSet') {
        // Ignore this line
    } else if (line.startsWith('- `')) {
        // Start capturing a nested command
        isCapturingNestedCommand = true;
        nestedCommandBuffer = line.substring(3);
    } else if (isCapturingNestedCommand) {
        if (line.endsWith('`')) {
            // End of nested command
            nestedCommandBuffer += line.substring(0, line.length - 1);
            currentCommand.commands.push(nestedCommandBuffer);
            isCapturingNestedCommand = false;
            nestedCommandBuffer = '';
        } else {
            // Continue capturing nested command
            nestedCommandBuffer += line + '\n';
        }
    } else {
        const mainCommandMatch = line.match(/^-\s*(\w+)(\d+)/);
        if (mainCommandMatch) {
            // Start a new main command
            const [, cmdType, cmdNumber] = mainCommandMatch;
            currentCommand = { type: cmdType, number: cmdNumber, commands: [] };
            dynamicCmd.sections[dynamicCmd.sections.length - 1].commands.push(currentCommand);
        } else if (line.startsWith('-') && currentCommand) {
            // Add a sub-command to the current main command
            currentCommand.commands.push(line.substring(1).trim());
        }
    }

    return { dynamicCmd, currentCommand, isCapturingNestedCommand, nestedCommandBuffer };
}

/**
 * Parses the entire input text into a structured dynamicCmd object.
 * @param {string} inputText - The input text to parse
 * @returns {Object} The parsed dynamicCmd object
 */
function parseInputText(inputText) {
    // Split input into lines
    const lines = inputText.split('\n');

    // Initialize parsing state
    let dynamicCmd = { sections: [] };
    let currentCommand = null;
    let isCapturingNestedCommand = false;
    let nestedCommandBuffer = '';

    // Process each line
    lines.forEach((line) => {
        // Update state based on processed line
        ({ dynamicCmd, currentCommand, isCapturingNestedCommand, nestedCommandBuffer } =
            processLine(line, dynamicCmd, currentCommand, isCapturingNestedCommand, nestedCommandBuffer));
    });

    return dynamicCmd;
}

/**
 * Main entry point for processing messages in Node-RED.
 * @param {Object} msg - The message object from Node-RED
 * @returns {Array} Array containing the processed message and two null values
 */
function processMessage(msg) {
    if (msg.payload && typeof msg.payload === 'string' && msg.payload.includes('dynamicCmd')) {
        // Parse the payload if it's a string containing 'dynamicCmd'
        msg.dynamicCmd = parseInputText(msg.payload);
    } else {
        // Set dynamicCmd to false for non-matching messages
        msg.dynamicCmd = false;
    }
    return [msg, null, null];
}

// Node-RED function node execution
