/**
 * Waits for a processed result to be ready in the flow context.
 * @param {number} timeout - Maximum time to wait in milliseconds
 * @returns {Promise<any>} The processed result
 */
async function waitForProcessedResult(timeout = 30000) {
    console.log('[waitForProcessedResult] Waiting for processed result');
    const startTime = Date.now();

    /**
     * Checks if the result is ready in the flow context.
     * @returns {Object|null} The result object if ready, null otherwise
     */
    function checkResultReady() {
        const currentContext = flow.get('flowContext') || {};
        if (currentContext.processedResultReady) {
            console.log('[waitForProcessedResult] Result ready');
            currentContext.processedResultReady = false;
            flow.set('flowContext', currentContext);
            return currentContext.processedResult;
        }
        return null;
    }

    /**
     * Checks if the timeout has been exceeded.
     * @throws {Error} If the timeout is exceeded
     */
    function checkTimeout() {
        if (Date.now() - startTime > timeout) {
            throw new Error('Timeout waiting for processed result');
        }
    }

    /**
     * Waits for a short period before the next check.
     * @returns {Promise<void>}
     */
    async function waitBeforeNextCheck() {
        await new Promise(resolve => setTimeout(resolve, 500));
    }

    /**
     * Main loop to wait for the processed result.
     * @returns {Promise<any>} The processed result
     */
    async function waitLoop() {
        while (true) {
            const result = checkResultReady();
            if (result !== null) {
                return result;
            }
            checkTimeout();
            await waitBeforeNextCheck();
        }
    }

    // Execute the wait loop
    return await waitLoop();
}

/**
 * Executes a single command and waits for its result.
 * @param {Object} command - The command to execute
 * @returns {Promise<any>} The result of the command execution
 */
async function executeCommand(command) {
    node.send({
        dynamicCmd: true,
        command: command,
        triggerCommandExecution: Date.now()
    });

    return await waitForProcessedResult(60000);
}

/**
 * Executes all commands in a dynamicCmd structure.
 * @param {Object} dynamicCmd - The dynamicCmd structure containing commands to execute
 * @returns {Promise<string>} The final result of all command executions
 */
async function executeBlockCommands(dynamicCmd) {
    /**
     * Initializes and updates the flow context.
     * @returns {Object} The initialized flow context
     */
    function initializeFlowContext() {
        let flowContext = flow.get('flowContext') || {};
        flowContext.previousResponse = flowContext.previousResponse || '[Previous response not available]';
        flow.set('flowContext', flowContext);
        return flowContext;
    }

    /**
     * Executes a single command and updates the flow context.
     * @param {Object} command - The command to execute
     * @param {Object} flowContext - The current flow context
     */
    async function executeSingleCommand(command, flowContext) {
        if (!command?.type) return;

        try {
            const result = await executeCommand(command);
            command.result = result;
            flowContext[`${command.type}${command.number}Result`] = result;
            flow.set('flowContext', flowContext);
            command.processedResult = result;
        } catch (error) {
            console.error(`[executeBlockCommands] Error executing ${command.type}:`, error);
            command.processedResult = `[Error: ${error.message}]`;
        }

        await new Promise(resolve => setTimeout(resolve, 100));
    }

    /**
     * Executes all commands in a section.
     * @param {Object} section - The section containing commands
     * @param {Object} flowContext - The current flow context
     */
    async function executeSection(section, flowContext) {
        if (!section?.commands?.length) return;

        for (const command of section.commands) {
            await executeSingleCommand(command, flowContext);
        }
    }

    /**
     * Main execution logic for block commands.
     */
    async function executeMain() {
        if (!dynamicCmd?.sections?.length) {
            throw new Error("Invalid dynamicCmd structure");
        }

        let flowContext = initializeFlowContext();

        for (const section of dynamicCmd.sections) {
            await executeSection(section, flowContext);
        }

        let finalResult = flowContext.mainPromptCmd1Result;
        msg.dynamicCmdComplete = true;
        return finalResult;
    }

    // Execute the main logic
    return await executeMain();
}

/**
 * Parses the incoming message and categorizes it.
 * @param {Object} msg - The incoming message
 * @returns {Object} Categorized message object
 */
function parseIncomingMessage(msg) {
    if (msg.dynamicCmd && !msg.currentCommand) {
        console.log('[parseIncomingMessage] New dynamic command detected');
        return { type: 'dynamicCmd', data: msg.dynamicCmd };
    } else if (msg.type === 'processedResult') {
        console.log('[parseIncomingMessage] Processed result received');
        return { type: 'processedResult', data: msg.data, command: msg.command };
    } else {
        console.log('[parseIncomingMessage] Pass through message');
        return { type: 'passThrough', data: msg };
    }
}

/**
 * Handles different types of messages based on their category.
 */
async function handleMessage() {
    console.log('[handleMessage] Starting message handling');
    try {
        const parsedMsg = parseIncomingMessage(msg);

        switch (parsedMsg.type) {
            case 'dynamicCmd':
                await handleDynamicCommand(parsedMsg.data);
                break;
            case 'processedResult':
                handleProcessedResult(parsedMsg.data);
                break;
            default:
                console.log('[handleMessage] Passing through message');
                node.send(msg);
        }
    } catch (error) {
        console.error("[handleMessage] Error:", error);
        node.error("Error in handleMessage: " + error.message, msg);
    }
}

