// Utility function for logging
const log = (prefix, message, obj = null) => {
    console.log(`[${prefix}] ${message}${obj ? ': ' + JSON.stringify(obj) : ''}`);
};

// Parse dynamic variables in text
function parseDynamicVariables(text, context) {
    log('parseDynamicVariables', 'Starting function with text:', text);

    if (typeof text !== 'string') {
        log('parseDynamicVariables', 'Warning: text is not a string. Type:', typeof text);
        if (text === null || text === undefined) {
            log('parseDynamicVariables', 'Text is null or undefined. Returning empty string.');
            return '';
        }
        try {
            text = String(text);
            log('parseDynamicVariables', 'Converted text to string:', text);
        } catch (error) {
            log('parseDynamicVariables', 'Error converting text to string:', error);
            return '[Error: Unable to process non-string input]';
        }
    }

    if (!msg || typeof msg !== 'object') {
        log('parseDynamicVariables', 'Warning: msg is not a valid object');
        return text;
    }

    try {
        // Handle previousResponse and sidePromptResult
        if (context && context.latestMsg && typeof context.latestMsg.currentStep === 'number') {
            const currentStep = context.latestMsg.currentStep;
            const previousStep = currentStep - 1;
            const previousResponseKey = `response${previousStep}`;

            if (previousStep >= 0 && context.latestMsg[previousResponseKey]) {
                const previousResponse = context.latestMsg[previousResponseKey];
                log('parseDynamicVariables', `Found previousResponse for step ${previousStep}:`, previousResponse);
                text = text.replace(/{previousResponse}/g, previousResponse);
            } else {
                log('parseDynamicVariables', `No previousResponse found for step ${previousStep}`);
                text = text.replace(/{previousResponse}/g, '[Previous response not available]');
            }
        } else {
            log('parseDynamicVariables', 'Unable to determine previousResponse from context');
            text = text.replace(/{previousResponse}/g, '[Previous response not available]');
        }

        log('parseDynamicVariables', 'After replacing previousResponse:', text);
        text = text.replace(/{sidePromptResult}/g, context.sidePromptResult || '[Side prompt result not available]');

        // Replace $<<commandTypeNumberResult>>$
        text = text.replace(/\$<<(\w+)(\d+)Result>>\$/g, (match, cmdType, cmdNumber) => {
            const resultKey = `${cmdType}${cmdNumber}Result`;
            const result = context[resultKey] || `[${resultKey} not available]`;
            log('parseDynamicVariables', `Replaced ${match} with:`, result);
            return result;
        });

        // Replace $<<execute(...)>>$
        text = text.replace(/\$<<execute\((.*?)\)>>\$/g, (match, code) => {
            log('parseDynamicVariables', 'Executing code:', code);
            try {
                code = code.replace(/;$/, '');
                const safeContext = createSafeContext(msg, context);
                const result = new Function('context', `with(context) { return (${code}); }`)(safeContext);
                log('parseDynamicVariables', 'Execution result:', result);
                return result !== undefined ? result : '[undefined]';
            } catch (error) {
                log('parseDynamicVariables', 'Error executing:', error.message);
                return `[Error executing: ${error.message}]`;
            }
        });

        // Handle regular msg property access
        text = text.replace(/\$<<([\w.]+)>>\$/g, (match, path) => {
            log('parseDynamicVariables', 'Accessing msg property:', path);
            const parts = path.split('.');
            let value = msg;
            for (const part of parts) {
                if (value && typeof value === 'object' && part in value) {
                    value = value[part];
                } else {
                    log('parseDynamicVariables', `Warning: ${path} not available in msg`);
                    return `[${path} not available]`;
                }
            }
            const result = typeof value === 'object' ? JSON.stringify(value) : String(value);
            log('parseDynamicVariables', 'Msg property value:', result);
            return result;
        });

        log('parseDynamicVariables', 'Final result:', text);
        return text;
    } catch (error) {
        log('parseDynamicVariables', 'Unexpected error:', error);
        return `[Error: ${error.message}]`;
    }
}


// Create a safe context for command execution
function createSafeContext(msg, context) {
    return {
        flow: {
            get: (key) => {
                const value = flow.get(key);
                log('createSafeContext', `flow.get('${key}'):`, value);
                return value;
            },
            set: (key, value) => {
                log('createSafeContext', `flow.set('${key}'):`, value);
                flow.set(key, value);
            }
        },
        global: {
            get: (key) => {
                const value = global.get(key);
                log('createSafeContext', `global.get('${key}'):`, value);
                return value;
            },
            set: (key, value) => {
                log('createSafeContext', `global.set('${key}'):`, value);
                global.set(key, value);
            }
        },
        msg: msg,
        context: context
    };
}

// Execute local command
function executeLocalCmd(command, msg, context) {
    log('executeLocalCmd', 'Executing local command:', command);
    let result = '';
    try {
        const commandString = command.commands.join('\n');
        log('executeLocalCmd', 'Executing:', commandString);
        const safeContext = createSafeContext(msg, context);
        const wrappedCommand = `with(this) { ${commandString} }`;
        const commandResult = new Function(wrappedCommand).call(safeContext);
        result = commandResult !== undefined ? commandResult : '';
    } catch (error) {
        log('executeLocalCmd', 'Error:', error.message);
        result = `[Error: ${error.message}]`;
    }
    log('executeLocalCmd', 'Result:', result);
    return result;
}

// Execute compile or pack command
function executeCompileOrPackCmd(command, msg, context) {
    log('executeCompileOrPackCmd', 'Executing compile/pack command:', command);
    let result = '';

    if (command && Array.isArray(command.commands)) {
        result = command.commands.map(cmd => {
            return parseDynamicVariables(cmd, context);
        }).join('\n');
    } else {
        log('executeCompileOrPackCmd', 'Invalid command structure:', command);
        result = '[Error: Invalid command structure]';
    }

    log('executeCompileOrPackCmd', 'Result:', result);
    return result.trim();
}

// Execute main prompt command
function executeMainPromptCmd(command, msg, context) {
    log('executeMainPromptCmd', 'Executing main prompt command:', command);
    const result = command.commands.map(cmd => parseDynamicVariables(cmd, context)).join('\n');
    log('executeMainPromptCmd', 'Result:', result);
    return result;
}

// Wait for side prompt result
function waitForSidePromptResult(timeout = 100000) {
    log('waitForSidePromptResult', 'Starting to wait for side prompt result');
    return new Promise((resolve, reject) => {
        const startTime = Date.now();
        const checkSidePromptResult = () => {
            let sidePromptResult = flow.get('sidePromptResult') || {};
            if (flow.get('waitingForSidePrompt') == false) {
                log('waitForSidePromptResult', 'Side prompt result is ready');
                resolve(sidePromptResult);
            } else if (Date.now() - startTime > timeout) {
                log('waitForSidePromptResult', 'Timeout reached while waiting for side prompt result');
                reject(new Error('Timeout waiting for side prompt result'));
            } else {
                log('waitForSidePromptResult', 'Side prompt result not ready, checking again in 1000ms');
                setTimeout(checkSidePromptResult, 1300);
            }
        };
        checkSidePromptResult();
    });
}

// Execute side prompt command
async function executeSidePromptCmd(command, msg, context) {
    log('executeSidePromptCmd', 'Executing side prompt command:', command);
    const sidePromptCommands = command.commands;

    // Set flow level variable indicating side prompt processing
    flow.set('waitingForSidePrompt', true);

    // Execute side prompt commands
    const result = sidePromptCommands.map(cmd => parseDynamicVariables(cmd, context));

    log('executeSidePromptCmd', 'Sent side prompt, waiting for result');

    try {
        // Wait for the side prompt result
        const sidePromptResult = await waitForSidePromptResult();

        // Update flow context with side prompt result
        context.sidePromptResult = sidePromptResult;
        flow.set('flowContext', context);

        log('executeSidePromptCmd', 'Received side prompt result:', sidePromptResult);
        return sidePromptResult;
    } catch (error) {
        log('executeSidePromptCmd', 'Error waiting for side prompt result:', error);
        return `[Error: ${error.message}]`;
    } finally {
        // Reset the processing flag
        flow.set('waitingForSidePrompt', false);
    }
}

// Execute command based on its type
function executeCommand(command, context) {
    log('executeCommand', 'Executing command:', command);
    let result = '';

    switch (command.type) {
        case 'localCmd':
            result = executeLocalCmd(command, msg, context);
            break;
        case 'compileCmd':
        case 'packCmd':
            result = executeCompileOrPackCmd(command, msg, context);
            break;
        case 'mainPromptCmd':
            result = executeMainPromptCmd(command, msg, context);
            break;
        case 'sidePromptCmd':
            result = executeSidePromptCmd(command, msg, context);
            break;
        default:
            log('executeCommand', 'Unknown command type:', command.type);
            result = `[Unknown command type: ${command.type}]`;
    }

    log('executeCommand', 'Result:', result);
    return result;
}

// Determine output type based on command type
function determineOutputType(commandType) {
    switch (commandType) {
        case 'sidePromptCmd': return 'sidePrompt';
        case 'mainPromptCmd': return 'mainPrompt';
        default: return 'dynamicCmd';
    }
}

// Update flow context with command result
function updateFlowContext(flowContext, command, result) {
    flowContext.processedResult = result;
    flowContext.processedResultReady = true;
    flowContext[`${command.type}${command.number}Result`] = result;
    flow.set('flowContext', flowContext);
    log('updateFlowContext', `Updated flowContext with result for ${command.type}${command.number}`);
}

// Main execution function
function main() {
    log('main', 'Starting main function');

    // Handle non-dynamic commands
    if (!msg.dynamicCmd) {
        log('main', 'Passing through non-dynamic command');
        return { ...msg, outputType: 'passThrough' };
    }

    const { command, triggerCommandExecution } = msg;
    log('main', 'Received command', command);
    log('main', 'triggerCommandExecution', triggerCommandExecution);

    // Handle invalid dynamic commands
    if (!command || !triggerCommandExecution) {
        log('main', 'Invalid dynamic command, passing through');
        return msg;
    }

    // Process valid dynamic command
    log('main', 'Processing dynamic command');
    let finalResult, outputType = 'dynamicCmd';

    try {
        // Execute command and determine output type
        finalResult = executeCommand(command, flow.get('flowContext') || {});
        outputType = determineOutputType(command.type);
    } catch (error) {
        log('main', 'Error executing command', error);
        finalResult = `[Error: ${error.message}]`;
        outputType = 'error';
    }

    log('main', 'Final result', finalResult);
    log('main', 'Output type', outputType);

    // Update flow context
    updateFlowContext(flow.get('flowContext') || {}, command, finalResult);

    // Return processed result
    return {
        ...msg,
        processedResult: { type: 'processedResult', data: finalResult, command },
        outputType
    };
}

// Main execution with error handling
try {
    return main();
} catch (error) {
    log('commandExecutor', 'Error in main function', error);
    return msg;
}

// Output Handler Node

// Utility function for logging
const log = (prefix, message, obj = null) => {
    console.log(`[${prefix}] ${message}${obj ? ': ' + JSON.stringify(obj) : ''}`);
};

// Determine the type of output based on the message content
function determineOutputType(msg) {
    log('determineOutputType', 'Starting output type determination', msg);

    // Check for invalid message
    if (typeof msg !== 'object' || msg === null) {
        log('determineOutputType', 'Invalid message object');
        return 'passThrough';
    }

    // Check for explicitly set output type
    if (msg.outputType) {
        log('determineOutputType', `Output type explicitly set: ${msg.outputType}`);
        return msg.outputType;
    }

    // Check processed result
    if (msg.processedResult?.type === 'processedResult') {
        const commandType = msg.processedResult.command?.type;
        if (commandType === 'sidePromptCmd' || commandType === 'mainPromptCmd') {
            log('determineOutputType', `${commandType} detected`);
            return commandType === 'sidePromptCmd' ? 'sidePrompt' : 'mainPrompt';
        }
    }

    // Check dynamic command
    try {
        const dynamicCmd = flow.get('dynamicCmd');
        log('determineOutputType', 'DynamicCmd', dynamicCmd);
        const lastCommand = dynamicCmd?.sections?.slice(-1)[0]?.commands?.slice(-1)[0];
        if (lastCommand) {
            log('determineOutputType', `Last command type: ${lastCommand.type}`);
            if (lastCommand.type === 'sidePromptCmd' || lastCommand.type === 'mainPromptCmd') {
                return lastCommand.type === 'sidePromptCmd' ? 'sidePrompt' : 'mainPrompt';
            }
        }
    } catch (error) {
        log('determineOutputType', 'Error checking dynamic command', error);
    }

    // Check for undefined dynamicCmd
    if (typeof flow.get('dynamicCmd') === 'undefined') {
        if (msg.sidePrompt === true) return 'sidePrompt';
        if (msg.mainPrompt === true) return 'mainPrompt';
    }

    // Default to passThrough
    log('determineOutputType', 'Defaulting to passThrough output type');
    return 'passThrough';
}

// Handle passThrough output
function handlePassThroughOutput(msg) {
    log('handlePassThroughOutput', 'Handling passThrough output', msg);
    return [msg, null, null];
}

// Handle side prompt output
function handleSidePromptOutput(msg) {
    log('handleSidePromptOutput', 'Handling side prompt output', msg);

    let sidePromptContent;
    if (msg.processedResult?.command?.type === 'sidePromptCmd' && Array.isArray(msg.processedResult.command.commands)) {
        sidePromptContent = msg.processedResult.command.commands;
    } else if (Array.isArray(msg.payload)) {
        sidePromptContent = msg.payload;
    } else {
        sidePromptContent = [msg.payload];
    }

    const sidePromptMsg = {
        ...msg,
        status: "start",
        sidePrompt: true,
        payload: sidePromptContent,
        sidePromptNumber: msg.processedResult?.command?.number,
    };

    log('handleSidePromptOutput', 'Formatted side prompt message', sidePromptMsg);
    return [null, sidePromptMsg, null];
}

// Handle main prompt output
function handleMainPromptOutput(msg) {
    log('handleMainPromptOutput', 'Starting to handle main prompt output');

    if (msg?.processedResult?.data) {
        log('handleMainPromptOutput', 'Found processedResult.data, moving to payload');
        msg.payload = msg.processedResult.data;
    } else {
        log('handleMainPromptOutput', 'processedResult.data not found in the message');
    }

    return [msg, null, null];
}

// Handle error output
function handleErrorOutput(msg) {
    log('handleErrorOutput', 'Handling error output', msg);
    return [null, null, msg];
}

// Garbage collection
function garbageCollect() {
    log('garbageCollect', 'Starting garbage collection');
    flow.keys().forEach(key => {
        flow.set(key, undefined);
        log('garbageCollect', `Cleared flow variable: ${key}`);
    });
    log('garbageCollect', 'Garbage collection completed');
}

// Main execution logic
function processOutput(msg) {
    log('Output Handler', 'Starting main execution logic');

    // Check for parsed execution result
    const parsedExecutionResult = flow.get('parsedExecutionResult');
    if (parsedExecutionResult) {
        log('Output Handler', 'parsedExecutionResult found', parsedExecutionResult);
        msg.payload = parsedExecutionResult;
    }

    // Determine output type
    const outputType = determineOutputType(msg);
    log('Output Handler', `Determined output type: ${outputType}`);

    // Garbage collection if dynamic command is completed
    if (msg.dynamicCmd !== false && msg.dynamicCmdComplete === true) {
        log('Output Handler', 'Dynamic command completed, triggering garbage collection');
        garbageCollect();
    }

    // Store last output type
    flow.set('lastOutputType', outputType);

    // Handle output based on type
    log('Output Handler', `Switching based on output type: ${outputType}`);
    let result;
    switch (outputType) {
        case 'passThrough':
            result = handlePassThroughOutput(msg);
            break;
        case 'dynamicCmd':
            log('Output Handler', 'Handling dynamicCmd (no action)');
            result = [null, null, null];
            break;
        case 'sidePrompt':
            result = handleSidePromptOutput(msg);
            break;
        case 'mainPrompt':
            result = handleMainPromptOutput(msg);
            break;
        case 'error':
            result = handleErrorOutput(msg);
            break;
        default:
            log('Output Handler', `Unknown output type: ${outputType}, treating as passThrough`);
            result = handlePassThroughOutput(msg);
    }

    log('Output Handler', 'Final result', result);
    return result;
}

// Execute the main function
return processOutput(msg);

// Use node context to persist state across messages
let isProcessing = context.get('isProcessing') || false;
let prompts = context.get('prompts') || [];
let currentStep = context.get('currentStep') || 0;

function processPrompts(inputPrompts) {
    console.log('[Sequence Controller Side] Processing input prompts');

    if (!Array.isArray(inputPrompts)) {
        console.error('[Sequence Controller Side] Error: Input is not an array');
        return [];
    }

    const processedPrompts = inputPrompts.map((prompt, index) => ({
        title: `Prompt ${index + 1}`,
        body: prompt,
        sidePrompt: []
    }));

    context.set('prompts', processedPrompts);
    console.log(`[Sequence Controller Side] Processed ${processedPrompts.length} prompts`);
    node.status({ fill: "blue", shape: "dot", text: `Loaded ${processedPrompts.length} prompts` });

    return processedPrompts;
}

function processNextPrompt() {
    console.log(`[Sequence Controller Side] Processing next prompt. Current step: ${currentStep + 1}`);
    if (currentStep < prompts.length) {
        const msg = {
            currentStep: currentStep,
            totalSteps: prompts.length,
            promptConfig: prompts[currentStep],
            payload: prompts[currentStep].body,
            sidePrompt: prompts[currentStep].sidePrompt,
            _continuation: true
        };
        console.log(`[Sequence Controller Side] Sending prompt ${currentStep + 1} of ${prompts.length}`);
        node.send(msg);
        node.status({ fill: "blue", shape: "dot", text: `Processing prompt ${currentStep + 1} of ${prompts.length}` });
        currentStep++;
        context.set('currentStep', currentStep);
    } else {
        console.log('[Sequence Controller Side] All prompts processed. Stopping.');
        node.send([null, { payload: "Sequence Complete", status: "sidePromptCmdDone" }]);
        stopProcessing();
    }
}

function startProcessing() {
    console.log('[Sequence Controller Side] Starting processing');
    isProcessing = true;
    currentStep = 0;
    context.set('isProcessing', isProcessing);
    context.set('currentStep', currentStep);
    processNextPrompt();
}

function stopProcessing() {
    console.log('[Sequence Controller Side] Stopping processing');
    isProcessing = false;
    currentStep = 0;
    prompts = [];
    context.set('isProcessing', false);
    context.set('currentStep', 0);
    context.set('prompts', []);
    node.status({ fill: "yellow", shape: "dot", text: "Sequence Stopped, Prompts Unloaded" });
}

// Main logic
if (msg._continuation) {
    console.log(`[Sequence Controller Side] Received continuation message with payload: ${msg.payload}`);
    if (isProcessing) {
        if (msg.status === "done" || msg.status === "End of Sequence") {
            console.log('[Sequence Controller Side] Received "done" or "End of Sequence" signal. Processing next prompt.');
            processNextPrompt();
        } else if (msg.status === "error") {
            console.log(`[Sequence Controller Side] Error occurred: ${msg.error}. Stopping sequence.`);
            stopProcessing();
        }
    }
} else {
    console.log(`[Sequence Controller Side] Received new message with payload: ${JSON.stringify(msg.payload)}`);
    if (msg.status === "start") {
        if (Array.isArray(msg.payload)) {
            prompts = processPrompts(msg.payload);
            startProcessing();
        } else {
            console.log('[Sequence Controller Side] Invalid input: payload is not an array of prompts');
            node.status({ fill: "red", shape: "ring", text: "Invalid input" });
        }
    } else if (msg.status === "stop") {
        stopProcessing();
    } else if (msg.status === "Sequence Complete") {
        node.send([null, { ...msg, status: "sidePromptCmdDone" }]);
        stopProcessing();
    } else {
        console.log(`[Sequence Controller Side] Unrecognized message: ${JSON.stringify(msg)}`);
    }
}

return null;

console.log('[Run Prompt Side] Starting execution');

node.status({ fill: "blue", shape: "dot", text: "Running..." });

context.runPrompt = context.runPrompt || {};
context.runPrompt.isStopRequested = msg.payload === "stop";

console.log('context.runPrompt:', context.runPrompt);

let browser, browserContext, currentPage;
console.log('[Run Prompt Side] Initial state:', { browser, browserContext, currentPage });

async function connectAndSetupPage() {
    console.log('[Run Prompt Side] Entering connectAndSetupPage');
    if (!browser) {
        console.log('[Run Prompt Side] Browser not initialized, connecting...');
        browser = await playwright.chromium.connectOverCDP('http://localhost:27000');
        console.log('[Run Prompt Side] Browser connected');
        browserContext = browser.contexts()[0];
        console.log('[Run Prompt Side] Browser context obtained');
        currentPage = browserContext.pages()[0] || await browserContext.newPage();
        console.log('[Run Prompt Side] Current page set');
    }
    //    console.log('[Run Prompt Side] Exiting connectAndSetupPage:', { browser, browserContext, currentPage });
    return { browser, browserContext, currentPage };
}

async function getCurrentPage() {
    console.log('[Run Prompt Side] Entering getCurrentPage');
    if (!browserContext) {
        console.log('[Run Prompt Side] Browser context not found, creating new one');
        browser = await playwright.chromium.connectOverCDP('http://localhost:27000');
        browserContext = browser.contexts()[0];
        console.log('[Run Prompt Side] New browser context created');
    }
    const pages = await browserContext.pages();
    console.log('[Run Prompt Side] Number of pages:', pages.length);
    const currentPage = pages[pages.length - 1];
    //console.log('[Run Prompt Side] Exiting getCurrentPage, current page:', currentPage);
    return currentPage;
}

async function clickButtonIfVisible(selector, waitForHidden = false) {
    try {
        const page = await getCurrentPage();
        const button = await page.$(selector);
        const shouldClick = button && await button.isVisible();

        if (shouldClick) {
            await button.click();

            if (waitForHidden) {
                await page.waitForSelector(selector, { state: 'hidden', timeout: 60000 });
            }
        }

        return shouldClick;
    } catch (error) {
        return false;
    }
}

async function reloadPageIfNecessary(retryCount) {
    console.log('[Run Prompt Side] Entering reloadPageIfNecessary:', { retryCount });
    try {
        const page = await getCurrentPage();
        const shouldReload = [
            retryCount > 0,
            !await clickButtonIfVisible('button:has-text("Continue generating")', true),
            await page.$('#prompt-textarea').then(() => false).catch(() => true)
        ].every(Boolean);

        console.log('[Run Prompt Side] Should reload:', shouldReload);
        shouldReload && await page.reload({ waitUntil: 'networkidle' });
        console.log('[Run Prompt Side] Page reloaded:', shouldReload);
    } catch (error) {
        console.log(`[Run Prompt Side] Error in reloadPageIfNecessary: ${error.message}`);
    }
}

async function waitForResponseCompletion() {
    console.log('[Run Prompt Side] Entering waitForResponseCompletion');
    console.log('[Run Prompt Side] Initializing variables');

    let previousLength = 0, currentLength = 0, stableCount = 0;
    const maxStableCount = 3;
    const maxWaitTime = 120000; // 2 minutes
    const startTime = Date.now();
    let responseText = '';

    console.log(`[Run Prompt Side] Max stable count: ${maxStableCount}, Max wait time: ${maxWaitTime}ms`);

    while (Date.now() - startTime < maxWaitTime) {
        console.log(`[Run Prompt Side] Elapsed time: ${Date.now() - startTime}ms`);

        if (context.runPrompt.isStopRequested) {
            console.log('[Run Prompt Side] Stop requested. Halting response completion.');
            await stopGeneration();
            clearStopRequestedState();
            return '';
        }

        try {
            console.log('[Run Prompt Side] Attempting to get current page');
            const page = await getCurrentPage();
            console.log('[Run Prompt Side] Successfully got current page');

            // Check for scroll-to-bottom button and click if visible
            console.log('[Run Prompt Side] Checking for scroll-to-bottom button');
            const scrollButtonSelector = 'button.cursor-pointer.absolute.z-10.rounded-full.bg-clip-padding.border.text-token-text-secondary.border-token-border-light.right-1\\/2.translate-x-1\\/2.bg-token-main-surface-primary.w-8.h-8.flex.items-center.justify-center.bottom-5';
            const scrollButtonClicked = await clickButtonIfVisible(scrollButtonSelector);

            if (scrollButtonClicked) {
