                    const markdownDiv = article.querySelector('.markdown.prose.w-full.break-words.dark\\:prose-invert.dark');
                    return markdownDiv ? markdownDiv.innerText : '';
                }, lastArticle);
                console.log('[Run Prompt Side] Content extracted successfully');
            } catch (error) {
                console.log('[Run Prompt Side] Error extracting content:', error.message);
                console.log('[Run Prompt Side] Falling back to article.innerText()');
                responseText = await lastArticle.innerText();
                console.log('[Run Prompt Side] Fallback content extraction complete');
            }

            currentLength = responseText.length;
            console.log(`[Run Prompt Side] Current response length: ${currentLength}`);
            if (currentLength == 0) {
                console.log('[Run Prompt Side] No content found in the response');
            }

            const isStable = currentLength === previousLength && currentLength > 0;
            stableCount = isStable ? stableCount + 1 : 0;
            console.log(`[Run Prompt Side] Is response stable: ${isStable}, Stable count: ${stableCount}`);

            if (stableCount >= maxStableCount) {
                console.log('[Run Prompt Side] Response appears to be complete');
                return responseText;
            }

            previousLength = currentLength;
            console.log('[Run Prompt Side] Waiting for 2 seconds before next iteration');
            await page.waitForTimeout(2000);
        } catch (error) {
            console.log(`[Run Prompt Side] Error in waitForResponseCompletion: ${error.message}`);
            console.log('[Run Prompt Side] Error stack:', error.stack);
            console.log('[Run Prompt Side] Waiting for 3 seconds before retrying');
            await new Promise(resolve => setTimeout(resolve, 3000));
        }
    }

    console.log('[Run Prompt Side] Max wait time exceeded. Returning current response.');
    return responseText;
}

async function extractResponseText(currentPage) {
    console.log('[Run Prompt Side] Entering extractResponseText');
    let responseText = '';
    let lastArticle;

    try {
        // Find all articles within the h-full div
        const articles = await currentPage.$$('div.h-full article.w-full.text-token-text-primary');
        console.log(`[Run Prompt Side] Found ${articles.length} articles`);

        if (articles.length > 0) {
            // Get the last article
            lastArticle = articles[articles.length - 1];
            console.log('[Run Prompt Side] Selected the last article');
        } else {
            throw new Error('No articles found');
        }

        // Find and click the copy button
        const copyButton = await lastArticle.$('button[aria-label="Copy"]');
        if (copyButton) {
            await copyButton.click();
            await currentPage.waitForTimeout(1000); // Wait for clipboard to be updated

            // Get clipboard contents
            responseText = await currentPage.evaluate(() => navigator.clipboard.readText());
            console.log('[Run Prompt Side] Response extracted from clipboard');
        } else {
            console.log('[Run Prompt Side] Copy button not found, falling back to original method');
            const markdownDiv = await lastArticle.$('div.markdown');
            if (markdownDiv) {
                responseText = await markdownDiv.innerText();
            } else {
                throw new Error('Markdown div not found');
            }
        }
    } catch (error) {
        console.log('[Run Prompt Side] Error extracting content:', error.message);
        try {
            if (!lastArticle) {
                lastArticle = await currentPage.$('div.h-full article.w-full.text-token-text-primary:last-child');
                if (!lastArticle) {
                    throw new Error('Unable to find last article');
                }
            }
            const textBaseElement = await lastArticle.$('.text-base');
            if (textBaseElement) {
                responseText = await textBaseElement.innerText();
                console.log('[Run Prompt Side] Response extracted using alternative selector');
            } else {
                throw new Error('Alternative selector not found');
            }
        } catch (innerError) {
            console.log('[Run Prompt Side] Alternative selector also failed, using entire article content');
            if (lastArticle) {
                responseText = await lastArticle.innerText();
            } else {
                console.log('[Run Prompt Side] Failed to extract any content');
                responseText = '';
            }
        }
    }

    console.log('[Run Prompt Side] Exiting extractResponseText, response length:', responseText.length);
    return responseText;
}

async function stopGeneration() {
    console.log('[Run Prompt Side] Entering stopGeneration');
    context.runPrompt.isStopRequested = true;
    await clickButtonIfVisible('button[data-testid="stop-button"][aria-label="Stop streaming"]', true);
    clearStopRequestedState();
    console.log('[Run Prompt Side] Exiting stopGeneration');
}

function clearStopRequestedState() {
    context.runPrompt.isStopRequested = false;
    console.log('[Run Prompt Side] Stop requested state cleared.');
}

function cleanup(browser, result, msg) {
    if (browser) {
        browser.close().then(() => console.log('[Run Prompt Side] Browser closed'));
    }

    node.status(result.status);

    const isLastMessage = result.endOfSequence;

    const firstOutput = {
        ...msg,
        payload: result.payload,
        endOfSequence: !!isLastMessage,
        _continuation: !isLastMessage  // Set _continuation to true for the last message
    };

    const secondOutput = {
        ...msg,
        payload: result.payload,
        status: result.status.text,
        _continuation: !isLastMessage,
        error: result.error,
        endOfSequence: isLastMessage === true
    };

    node.send([firstOutput, secondOutput]);

    return result;
}

async function runPrompt() {
    console.log('[Run Prompt Side] Entering runPrompt');
    try {
        await reloadPageIfNecessary(0);

        const page = await getCurrentPage();
        console.log('[Run Prompt Side] Got current page in runPrompt');

        // ... (existing code for new chat button)
        console.log('[Run Prompt Side] Full Msg:', msg);
        const promptText = msg.payload || 'Sidefiring';
        console.log('[Run Prompt Side] Entering prompt:', promptText);
        await page.waitForSelector('#prompt-textarea', { state: 'attached', timeout: 30000 });
        await page.fill('#prompt-textarea', promptText);

        // Handle sidePrompt if present
        if (msg.sidePrompt && msg.sidePrompt.length > 0) {
            console.log('[Run Prompt Side] Handling sidePrompt');
            const sidePromptText = msg.sidePrompt.join('\n');
            await page.evaluate((text) => {
                const textarea = document.querySelector('#prompt-textarea');
                textarea.value += '\n\n' + text;
                textarea.dispatchEvent(new Event('input', { bubbles: true }));
            }, sidePromptText);
        }

        await page.click('button[data-testid="send-button"]');
        console.log('[Run Prompt Side] Sent prompt');

        await waitForResponseCompletion();

        const responseText = await extractResponseText(page);
        await clickButtonIfVisible('button:has-text("I prefer this response")');
        await page.waitForSelector('button[data-testid="stop-button"]', { state: 'hidden', timeout: 120000 });

        console.log('[Run Prompt Side] Exiting runPrompt');
        return responseText;
    } catch (error) {
        console.log(`[Run Prompt Side] Error in runPrompt: ${error.message}`);
        console.log('[Run Prompt Side] Error stack:', error.stack);
        throw error;
    }
}


async function setupBrowser() {
    try {
        const { browser } = await connectAndSetupPage();
        return browser;
    } catch (error) {
        throw new Error('Connection setup error: ' + error.message);
    }
}

function isStopRequested() {
    return context.runPrompt.isStopRequested;
}

async function handleStopRequest(browser, msg) {
    await stopGeneration();
    return {
        payload: "stopped",
        status: { fill: "yellow", shape: "dot", text: "Stopped" }
    };
}

async function runPromptWithRetries(msg, maxRetries) {
    for (let retryCount = 0; retryCount < maxRetries; retryCount++) {
        if (isStopRequested()) {
            return handleStopRequest();
        }

        try {
            const payload = await runPrompt();
            return {
                payload,
                status: { fill: "green", shape: "dot", text: "done" }
            };
        } catch (error) {
            console.error(`[Run Prompt Side] Attempt ${retryCount + 1} failed:`, error);
            await reloadPageIfNecessary(retryCount + 1);

            if (retryCount === maxRetries - 1) {
                throw error;
            }
        }
    }
    throw new Error('Max retries reached');
}

function isLastStep(msg) {
    console.log('This is the last step');
    return msg.currentStep === msg.totalSteps - 1;
}

function handleLastStep(result) {
    return {
        ...result,
        endOfSequence: true,
        status: { fill: "green", shape: "dot", text: "Sequence Complete" }
    }; 
}

function handleError(error) {
    return {
        payload: 'Error occurred: ' + error.message,
        status: { fill: "red", shape: "ring", text: "Error" },
        error: error.message
    };
}

async function executeScript(msg) {
    console.log('[Run Prompt Side] Entering executeScript');
    const maxRetries = 3;
    let browser;
    let result = { payload: null, status: null, error: null };

    try {
        browser = await setupBrowser();

        if (isStopRequested()) {
            return handleStopRequest(browser, msg);
        }

        result = await runPromptWithRetries(msg, maxRetries);

        if (isLastStep(msg)) {
            result = handleLastStep(result);
            console.log('last step result: ', result);
        }
    } catch (error) {
        result = handleError(error);
    }

    return cleanup(browser, result, msg);
}



executeScript(msg);

return null;

msg.payload = msg.payload.map(flow => ({
    id: flow.id,
    label: flow.label || 'Unnamed Flow'
}));
return msg;
